css:
@-moz-document url-prefix("https://share.dmhy.org/") {
/* ad */
.ad,
#\31 280_adv,
#bebiv3_ad,
.kiwi-ad-wrapper-950x80 {
    display: none !important;
}

/* body */
body {
    background: #fcfcfc;
}

body::before {
    content: "";
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 50px;
    background: #fff;
    border-bottom: 1px solid #ccc;
    z-index: 888;
}

.bg {
    width: 100%;
    min-width: 100%;
    max-width: 100%;
    margin: 0 auto;
}

a, p, div, span, tr, th, td, li, input, select, option {
    font-size: 14px;
    font-family: "lucida grande", "lucida sans unicode", lucida, helvetica, "Hiragino Sans GB", "Microsoft YaHei", "WenQuanYi Micro Hei", sans-serif;
}

/* header */
.bg > .header {
    position: fixed;
    top: 0;
    left: calc(50% - 800px);
    width: 1600px;
    height: 50px;
    background: #fff;
    z-index: 999;
}

@media all and (max-width: 1600px) {
.bg > .header {
    left: 0;
    width: 100%;
}
}

.header > .headerleft {
    height: auto;
}

.header > .headerleft:after {
    content: "動漫花園資源網";
    position: fixed;
    margin: -30px 18px;
    font-size: 22px;
    font-weight: 500;
    letter-spacing: 0.5px;
    color: #38F;
    z-index: -1;
}

.header > .headerleft > a {
    display: block;
    width: 160px;
    height: 25px;
    margin: 13px 0 0 16px;
}

.header > .headerleft > a > img {
    display: none;
}

/* login */
.header > .headerright {
    padding: 0;
    overflow: visible;
}
.header > .headerright > .links {
    color: rgba(0, 0, 0, 0) !important;
    background: rgba(0, 0, 0, 0) !important;
    cursor: default;
}

.header > .headerright > .links > a {
    display: none;
}

#login_cm {
    display: block;
}

#login_cm a {
    position: absolute;
    top: 9px;
    right: 0;
    padding: 6px 11px;
    border-radius: 8px;
    color: #333;
    letter-spacing: 1px;
    text-decoration: none;
    background: #f5f5f5;
    transition: background 0.3s ease-out;
}

#login_cm a:hover {
    background: #ddd;
    transition: background 0.3s ease-out;
}

@media all and (max-width: 1600px) {
#login_cm a {
    right: 10px;
}
}

/* navigation */
.top_sort {
    display: none;
}

/* main */
.main {
    width: 1600px;
    margin: 51px auto 30px auto;
    padding: 0;
    background: #fcfcfc;
}

/* all date */
.jmd {
    display: none !important;
}

.jmd_base {
    display: block !important;
}

/* anime date */
#mini_jmd {
    width: calc(100% - 2px) !important;
    margin-top: 30px;
    margin-bottom: 0;
    border: 1px solid #ccc;
    padding: 0;
    border-radius: 8px;
}

.nav_title {
    display: none;
}

.jmd_base tbody {
    width: 100%;
}

.jmd_base tbody td {
    padding: 5px 0;
}

.jmd_base > tbody > tr:nth-child(1) > td {
    padding-top: 10px;
}

.jmd_base > tbody > tr:nth-child(7) > td {
    padding-bottom: 10px;
}

.jmd_base tbody th {
    width: 100px;
    font-weight: 400;
}

.jmd_base tbody a {
    display: inline-block !important;
    margin: 3px;
    padding: 3px 8px;
    border-radius: 8px;
    font-size: 13px;
    text-decoration: none;
    color: #333;
    background: #f5f5f5;
    transition: background 0.3s ease-out;
}

.jmd_base tbody a:hover {
    background: #ddd;
    transition: background 0.3s ease-out;
}

/* search */
.quick_search {
    position: fixed;
    top: 9px;
    width: 1300px;
    margin-left: 210px;
    padding: 0;
    border: none;
    text-align: left;
    background: rgba(0, 0, 0, 0);
    z-index:999;
}

@media all and (max-width: 1600px) {
.quick_search {
    width: calc(100% - 320px);
}
}

#keyword {
    width: 1090px;
    padding: 6px 12px;
    border: none;
    border-radius: 8px;
    background: #f5f5f5;
    outline: none !important;
    transition: background 0.3s ease-out;
}

#keyword:hover, #keyword:focus {
    background: #ddd;
    transition: background 0.3s ease-out;
}

@media all and (max-width: 1600px) {
#keyword {
    width: calc(100% - 200px);
}
}

.formButton {
    margin: 0 0 0 7px;
    padding: 6px 18px;
    border: none;
    border-radius: 8px;
    color: #fff;
    letter-spacing: 1px;
    text-decoration: none;
    cursor: pointer;
    background: #38F;
    transition: background 0.3s ease-out;
}

.formButton:hover {
    background: #185ABC;
    transition: background 0.3s ease-out;
}

.quick_search > form > a {
    position: relative;
    top: 1px;
    margin: 0 0 0 6px;
    padding: 6px 18px 6px 23px;
    border-radius: 8px;
    color: #333;
    letter-spacing: 1px;
    text-decoration: none;
    cursor: pointer;
    background: #f5f5f5;
    transition: background 0.3s ease-out;
    transform: translateY(1px);
}

.quick_search > form > a:hover {
    background: #ddd;
    transition: background 0.3s ease-out;
}

.quick_search > form > a > span {
    display: none;
}

/* advanced search */
#AdvSearch {
    display: none;
    position: fixed;
    top: 62px;
    padding: 6px 41px 6px 22px;
    border: 1px solid #ccc;
    border-radius: 8px;
    box-shadow: 0 8px 25px rgba(0, 0, 0, 0.3);
    background: #fff;
    z-index: 999;
}

/*
#AdvSearch:before {
    content: '';
    display: block;
    position: fixed;
    left: 0;
    top: 51px;
    width: 100%;
    height: 50px;
    border-bottom: 1px solid #ccc;
    background: #fff;
    z-index: -1;
}
*/

#AdvSearch p {
    padding: 0 20px;
}

#AdvSearch select {
    border: none;
    margin-bottom: 4px;
    padding: 3px 5px 1px 5px;
    border-radius: 8px;
    outline: none !important;
    background: #f5f5f5;
    transition: background 0.3s ease-out;
}

#AdvSearch select:hover {
    background: #ddd;
    transition: background 0.3s ease-out;
}

#AdvSearch p:nth-child(3) {
    padding-right: 40px;
}

#AdvSearch p:last-child {
    padding: 3px 10px;
    border-radius: 8px;
    background: #f5f5f5;
    transition: background 0.3s ease-out;
}

#AdvSearch p:last-child:hover {
    background: #ddd;
    transition: background 0.3s ease-out;
}

#AdvSearch p:last-child a {
    color: #333;
    text-decoration: none;
}

/* content table */
.main >div > .table {
    margin-top: 30px;
    border: 1px solid #ccc;
    padding: 0;
    border-radius: 8px;
}

#topic_list, #topic_list tr, #topic_list td {
    border-spacing: 0;
    background-color: rgba(0, 0, 0, 0);
}

#topic_list thead th {
    padding: 12px 0;
    color: #333;
    background-color: #fcfcfc;
}

#topic_list th.headerSortUp, #topic_list th.headerSortDown {
    background-color: #ddd;
}

#topic_list > thead > tr > th:first-child {
    border-top-left-radius: 8px;
}

#topic_list > thead > tr > th:last-child {
    border-top-right-radius: 8px;
}

#topic_list tbody td {
    padding: 12px 10px;
    border-bottom: solid 1px #f5f5f5;
    line-height: 25px;
}

#topic_list tbody td .tag {
    padding: 0px 6px !important;
    border: none !important;
    border-radius: 100px;
    background: #f5f5f5 !important;
    transition: background 0.3s ease-out;
}

#topic_list tbody td .tag:hover {
    background: #ddd !important;
    transition: background 0.3s ease-out;
}

#topic_list tbody td .tag a {
    color: #333;
    font-size: 12px;
}

#topic_list tbody td .tag a:hover {
    border-bottom: none;
    text-decoration: none;
}

#topic_list tbody .title > span {
    color: #aaa !important;
}

#topic_list tbody td a {
    color: #333;
}

#topic_list tbody td a:hover {
    border-bottom: none;
    text-decoration: underline;
}

#topic_list tbody .btl_1, #topic_list tbody .bts_1 {
    color: #333;
}

#topic_list .keyword {
    color: #F26;
    background: rgba(0, 0, 0, 0);
}

.download-arrow {
    color: #38F !important;
    font-size: 14px;
    margin: 0;
    padding: 2px 6px 0 8px;
    border-radius: 20px;
    background: rgba(0, 0, 0, 0);
    transition: background 0.3s ease-out;
}

.download-arrow:hover {
    text-decoration: none !important;
    background: #E8F0FE;
    transition: background 0.3s ease-out;
}

.arrow-magnet:before {
    content: "下載"; 
}

#topic_list thead th:nth-child(1) {
    width: 160px;
}

#topic_list thead th:nth-child(4) {
    width: 5%;
}

#topic_list thead th:nth-child(5) {
    width: 6%;
}

#topic_list thead th:nth-child(6), #topic_list thead th:nth-child(7), #topic_list thead th:nth-child(8) {
    width: 4%;
}

#topic_list thead th:nth-child(9) {
    width: 8%;
}

.main > .table > .clear > div {
    display: block;
    padding: 70px 0 !important;
    border-top-left-radius: 8px;
    border-top-right-radius: 8px;
    color: #333;
    text-align: center;
    background-color: #fff !important;
}

.main > .table > .clear > .nav_title {
    display: block;
    padding: 15px 0 !important;
    border-bottom-left-radius: 8px;
    border-bottom-right-radius: 8px;
    color: #aaa;
    text-align: center;
    background-color: #fcfcfc !important;
}

.main > .table > .clear > .nav_title > a {
    color: #333;
}

/* footer */
.footer,
div.main > div:nth-child(13),
div.main > div:nth-child(12),
div.main > div:nth-child(11),
div.main > div:nth-child(9) > div:nth-child(6),
div.main > div:nth-child(9) > div:nth-child(5),
div.main > div:nth-child(9) > div:nth-child(2) {
    display: none;
}
}

@-moz-document url-prefix("https://share.dmhy.org/topics/view/") {
/* anime date */
#mini_jmd {
    display: none;
}

/* post container */
div.topics_bk.ui-corner-all {
    margin-top: 510px;
    border: none;
    padding: 0;
    background: rgba(0, 0, 0, 0);
}

/* intro */
.topic-main > .topic-title {
    position: absolute;
    top: 140px;
    width: 1598px;
    margin: 0 0 0 -414px;
    padding: 0;
    border: 1px solid #ccc;
    border-radius: 8px;
    background: #fff;
    height: 338px;
}

.topic-main > .topic-title > h3 {
    margin-top: -60px;
    max-width: 1518px;
    padding: 0 40px;
    border: none;
    font-size: 18px;
    font-weight: 400;
    text-align: left;
    text-overflow:ellipsis;
    white-space: nowrap;
    overflow:hidden;
}

.topic-main > .topic-title > .resource-info {
    float: left;
    text-shadow: none;
}

.topic-main > .topic-title > .resource-info > ul {
    margin: 20px 40px;
}

.topic-main > .topic-title > .resource-info > ul > li {
    margin: 8px 0;
}

.topic-main > .topic-title > .resource-info > ul > li:nth-child(4) > a {
    color: #38F;
}

.topic-main > .topic-title > .resource-info > ul > li:nth-child(3),
.topic-main > .topic-title > .resource-info > ul > li:nth-child(6),
.topic-main > .topic-title > .resource-info > ul > li.share {
    display: none;
}

/* download */
#resource-tabs {
    position: absolute;
    top: 140px;
    width: 1198px;
    margin: 0 0 0 -14px;
    padding: 0;
    border: none;
    background: rgba(0, 0, 0, 0);
}

#resource-tabs > ul, #resource-tabs .warn, #tabs-1 > p:nth-child(8) {
    display: none;
}

#resource-tabs hr {
    margin: 20px 0 0 -399px;
    width: 1598px;
    border: 0;
    height: 1px;
    background: #f5f5f5;
}

#tabs-1 {
    padding: 0;
    margin-top: 20px;
    text-align: left;
}

#tabs-1 > p {
    width: 1160px;
    padding: 0;
    margin: 8px 0;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
}

#tabs-1 strong {
    font-weight: 400;
}

#tabs-1 > .file_list {
    margin: 20px 0 0 -360px;
    width: 1520px;
    padding: 0;
    border: none;
    height: 150px;
    min-height: 150px;
    max-height: 150px;
}

#tabs-1 > .file_list li {
    padding: 4px 20px 4px 2px; 
}

#tabs-1 > .file_list li img {
    margin: 0 5px 3px 0;
}

#tabs-1 > .file_list li span {
    margin-left: 8px;
    color: #aaa;
}

#tabs-1 > .file_list .even {
    background-color: #fff;
}

#tabs-1 > ::-webkit-scrollbar {
    width: 8px;
    border-radius: 4px;
    background: #fcfcfc;
}

#tabs-1 > ::-webkit-scrollbar-thumb {
    border-radius: 4px;
    background: #ddd;
}

#tabs-1 > ::-webkit-scrollbar-thumb:active {
        background: #888;
    }

/* user sidebar */
.user-sidebar {
    width: 380px;
    border: 1px solid #ccc;
    padding: 0;
    border-radius: 8px;
    background: #fff;
}

.user-sidebar .avatar {
    display: inline-block;
    width: 187px;
    margin: 18px 0;
    padding: 0;
    border: none;
    background: rgba(0, 0, 0, 0);
}

.user-sidebar .avatar p {
    margin: 0;
    color: #333;
    font-weight: 400;
}

.user-sidebar .avatar p a {
    color: #333;
}

.user-sidebar .avatar p:nth-child(2) {
    margin-top: 8px;
    text-align: center;
}

.user-sidebar .avatar p:last-child {
    display: none;
}

.user-sidebar .user_avatar_img, .user-sidebar .team_logo_img {
    width: 80px !important;
    height: 80px !important;
    border: none;
    border-radius: 8px;
}

.user-sidebar .topics_cult, .user-sidebar div:last-child {
    display: none
}

/* topic main */
.topic-main {
    width: 1186px;
    margin-left: 30px;
    border: 1px solid #ccc;
    padding: 0;
    border-radius: 8px;
    background: #fff;
}

.topic-main .info {
    text-align: left;
}

.topic-main .topic-nfo {
    margin: 0;
    padding: 18px 25px;
    border: none;
    background: rgba(0, 0, 0, 0);
}

.topic-main .topic-nfo > strong:nth-child(1),
.topic-main .topic-nfo > br:nth-child(2),
.topic-main .topic-nfo > br:last-child {
    display: none;
}

.topic-main .topic-nfo p {
    padding: 0;
    margin-bottom: 12px;
}

.topic-main .topic-nfo img {
    padding: 0;
}

/* comment */
#recent-commnet {
    position: absolute;
    top: 697px;
    width: 380px;
    margin-left: -413px;
    padding: 0;
    border: 1px solid #ccc;
    border-radius: 8px;
    background: #fff;
}

#recent-commnet > .declaration {
    display: none;
}

#comment_recent, #comment_recent tr {
    background: rgba(0, 0, 0, 0);
    border-spacing: 0;
}

#comment_recent tbody:after {
    content: "";
    display: block;
    height: 1px;
    width: 262%;
    background: #f5f5f5;
}
#comment_recent td {
    border-bottom: solid 1px #f5f5f5;
    border-right: none !important;
    background: rgba(0, 0, 0, 0) !important;
}

#comment_recent .infotable {
    position: relative;
    left: -1000px;
    width: 145px;
    padding: 0 !important;
    text-align: center;
    overflow: visible;
}

.infotable p {
    display: none;
}

.infotable span.username, .infotable span.date, .infotable span.ip {
    display: inline-block;
    position: relative;
    width: 145px;
    left: 1000px;
    text-align: center;
}

.infotable span.username {
    top: -22px;
    padding-top: 10px;
    border-top: solid 1px #f5f5f5;
    color: #333 !important;
}

#comment_recent tr:nth-child(1) span.username {
    border-top: none;
}

.infotable span.username {
    top: -22px;
    padding-top: 10px;
    border-top: solid 1px #f5f5f5;
    color: #333 !important;
}

.infotable span.date {
    top: -60px;
    color: #aaa !important;
    font-size: 12px;
    font-weight: 400 !important;
}

.infotable span.ip {
    display: none;
}

.comment_con {
    width: 235px !important;
    padding: 10px 12px !important;
    border-left: solid 1px #f5f5f5;
}

#comment_recent tr:last-child td {
    border-bottom: none;
}

.comment_con .title {
    display: none;
}

.comment_con span {
    word-wrap: break-word;
    word-break: break-all; 
}

/* add comment */
#comment_add {
    margin: 0;
    border-radius: 0;
    background: rgba(0, 0, 0, 0);
}

#comment_add td {
    background: rgba(0, 0, 0, 0);
}

#comment_add tr:nth-child(1), #comment_add tr td:nth-child(2){
    display: none;
}

#comment_add tr td:nth-child(1){
    padding: 35px 0 !important;
    color: #333;
    text-align: center;
}

#comment_add tr td:nth-child(1) a {
    color: #38F;
}
}
