css:
@-moz-document url-prefix("https://duckduckgo.com/?"), url-prefix("http://duckduckgo.com/?"), url-prefix("https://www.duckduckgo.com/?"), url-prefix("http://www.duckduckgo.com/?") {
/* Background */
.header-wrap {
  background-color: #fff;
}
.content-wrap {
  background-color: #f9f9f9;
}
body {
  background-color: #f9f9f9;
}


/*  Centering */
div.cw:nth-child(4),
#header,
div.zcm:nth-child(4) {
  margin-left: auto !important;
  margin-right: auto !important;
}

.header__search-wrap {
  margin-left: 23.5%;
  margin-right: 22%;
  max-width: none;
}

#duckbar {
  margin-left: -2.7%;
  margin-right: 36%;
  margin-bottom: -26px;
  font-size: 12px;
  max-width: none;
}

.results--main {
  margin-left: 31%;
  width: 40%;
  margin-right: auto;
}


#duckbar_static {
  margin-left: 45%;
  margin-right: auto;
}

.cw {
  max-width: none;
}



#links_wrapper {
  padding-left: 0px;
}

.zci__main {
  max-width: none;
}

.zci__body {
  margin-left: 26%;
  margin-right: auto;
  max-width: 40%;
}


.header--aside__item {
  display: none;
}

.results-wrapper {
  padding-right: 0 !important;
}

.header__button--menu {
  margin-right: 36%;
}

/* filters location*/

.search-filters-wrap {
  margin-top: -12px;
  margin-bottom: 16px;
  box-shadow: 0 7px 15px rgba(0, 0, 0, 0.04);
  background: #fff;
}

.dropdown__switch {
  margin-top: 12px;
}
.dropdown--region {
  margin-top: 2px
}
.dropdown__button {
  margin-top: 2px;
}

.sidebar-modules {
  margin-left: 26px;
  max-width: 340px;
  box-shadow: 0px 0px 1px 12px rgba(0, 0, 0, 0.01);
}

.modal__box {
  margin-left: 0.2%
}


div.zcm:nth-child(4) {
  width: 570px !important;
}

[class*="tileview"] {
  padding-left: 0 !important;
}

#zci-meanings > div:nth-child(1) {
  padding-bottom: 1em !important;
}

.has-active-zci .header-wrap::before,
.has-active-zci .header-wrap::after {
  visibility: hidden !important;
}

#zero_click_wrapper {
  margin-bottom: 1em !important;
}

#header_wrapper::after {
  height: 2em !important;
}

/*** Answers, definitions, etc. *********************************************/
#zci-answer .cw,
#zci-dictionary_definition .cw,
#zci-about .cw,
#zci-thesaurus .cw {
  margin-left: auto !important;
}

/*** Q&A ********************************************************************/
#zci-qa {
  display: flex !important;
}

#zci-qa > div:nth-child(1),
#zci-qa > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) {
  margin-left: auto !important;
  margin-right: auto !important;
}

#zci-qa > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) {
  max-width: 640px !important;
  margin-left: auto !important;
  margin-right: auto !important;
}

.has-tiles .tile:last-child,
.has-tiles .tile--s:last-child {
  -margin-right: 0 !important;
}

/*** Responsive *************************************************************/
@media only screen and (min-width: 425.1px) and (min-height: 382.6px),
only screen and (min-width: 634.1px) and (min-height: 361.30px) {}
@media only screen and (max-width: 425px) {}
@media only screen and (max-height: 382.5px) and (max-width: 634px),
only screen and (max-height: 361.25px) {}
@media only screen and (min-width: 864px) {}

/*******************/

/* highlight keywords */
.result__body b,
.result__body strong {
  background: #ffa;
}
.dark-bg .result__body b,
.dark-bg .result__body strong {
  color: #fff !important;
  background-color: #453 !important;
}

.result__body b,
.result__body strong {
  display: inline-block;
  padding: 0 0.15rem;
  margin: 0 -0.15rem;
  position: relative;
  z-index: -1;
}
.result__title,
.result__a {
  padding-left: 0.15rem;
  margin-left: -0.15rem;
}
.result {
  z-index: 0;
}


.result__a::after/* quick hack to avoid unnecessary ellipsis in chrome */
{
  margin-right: 0.15rem;
  content: '';
}

/*******************/

/* hide unnecessary*/
div[class="badge-link badge-link--top badge-link--fadein ddg-extension-hide js-badge-link "] {
  display: none !important;
}

div[class="badge-link badge-link--serp  ddg-extension-hide js-badge-link "] {
  display: none !important;
}

a[class="header--aside__msg sep--after js-side-menu-msg"] {
  display: none !important;
}

div[class="btn feedback-btn"] {
  display: none !important;
}

div[class="onboarding-ed js-onboarding-ed"] {
  display: none !important;
}

.feedback-prompt {

  right: -20px !important;
}

/* Add some padding inside the card container */
.result {
  padding: 2px 16px;
}

.result--sep {
  box-shadow: none !important;
}

.result__check {
  color: #f9f9f9 !important;
}
.result__check:visited {
  color: #aaf !important;
}


/*******************/

/* Boxing */
.result {
  box-shadow: 0 7px 15px rgba(0, 0, 0, 0.04);
  transition: 0.2s;
  background-color: #ffffff;
}

/* On mouse-over, add a deeper shadow */
.result:hover {
  box-shadow: 0 7px 15px rgba(0, 0, 0, 0.06);
  background-color: #ffffff;
}

.module {
  border: none;
}

.module--news {
  padding-top: 12px;
  padding-left: 2px;
  box-shadow: 0 7px 15px rgba(0, 0, 0, 0.04);
  transition: 0.2s;
  background-color: #fff;
}

.module--news .module__header,
.module--news .module__footer {
  margin-left: 4%;
}
.module--news__item {
  margin-left: 2%;
  height: 208px;
}

.module--news__body {
  margin-left: 0.6%;
}
.module--news__body__content {
  max-height: 5.5em;
}

.results--sidebar {
  margin-top: -0.9%;
}
.module--about {
  padding-top: 13px;
  padding-left: 2px;
  box-shadow: 0 2px 15px rgba(0, 0, 0, 0.02);
  transition: 0.2s;
  background-color: #fff;
  margin-bottom: 52px;
}
}
