css:
@-moz-document url(https://workflowy.com/) {
/* Variables and resets */
:root {
  /* White Theme Colors */
  /* Sepia Theme Colors */
  --primary-color: #f4ecd8 !important;
  --secondary-color: #5b4636 !important;
  --link-color: rgba(91, 70, 54, 0.5) !important;
  --bg-color: #ddd5c2 !important;
  --border-color: rgba(91, 70, 54, 0.2) !important;
  --highlight-color: #e9e4d8 !important;
  /* Dark Theme Colors */ }

/* List styles */
.project.collapsed > .name > .bullet {
  background: #ddd5c2 !important;
  background: var(--bg-color) !important; }

.open > .children {
  border-left: 1px dotted rgba(91, 70, 54, 0.2) !important;
  border-left: 1px dotted var(--border-color) !important; }

.highlighted::before {
  background: #e9e4d8 !important;
  background: var(--highlight-color) !important; }

/* Page styles */
/* Change icons */
.starButton,
.pageMenu > div {
  color: rgba(91, 70, 54, 0.5) !important;
  color: var(--link-color) !important; }
  .starButton:hover,
  .pageMenu > div:hover {
    background-color: #e9e4d8 !important;
    background-color: var(--highlight-color) !important; }

/* Change bottomlinks */
.bottomLink {
  font-style: normal !important; }

/* Remove share buttons and Go Pro link */
.facebookLike,
.twitter-follow-button,
.proPitch,
#proPitchBottomRight {
  display: none !important; }

#backgroundImage {
  background: #ddd5c2 !important;
  background: var(--bg-color) !important; }

.page {
  border: 1px solid rgba(91, 70, 54, 0.2) !important;
  border: 1px solid var(--border-color) !important;
  border-radius: 2px !important;
  background: #f4ecd8 !important;
  background: var(--primary-color) !important;
  color: #5b4636 !important;
  color: var(--secondary-color) !important; }

/* Tag-based components */
.contentTagText {
  text-decoration: none !important; }

[title*="#"] {
