css:
@-moz-document domain("static-course-assets.s3.amazonaws.com") {
.section-title {
    background-color: #282828!important;
    color: white!important;
}
* {
 border-radius: 0!important;   
}

.topic-title {
    background-color: #444444!important;
    color: white!important;
}
.module-index #nav {
    overflow-y: scroll!important;
    scrollbar-width: thin!important;
}
body
{
	background-color: #333 !important;
}
.module-index #content {
    background-color: #4c4b4b!important;
}
h1 {
    color: white!important;
    background-color: #282828!important;
}
#home-btn:hover {
    background-color: #232323!important;
}
#home-btn {
    background-color: #333333!important;
}
#header {
    background-color: #282828!important;
}
.module-index #content
{
  background-color: #333 !important;
}

h1
{
  color: #d0d0d0 !important;
}

#nav ul, #nav a
{
  color: #909090 !important;
}

.ui-layout-west #content
{
	background-color: #333 !important;
	color: #BaBaaa !important;
}
#canvas {
    border: 2px solid transparent;
	border-image: linear-gradient(to bottom right, #b827fc 0%, #2c90fc 25%, #b8fd33 50%, #fec837 75%, #fd1892 100%);
    border-image: -webkit-linear-gradient(to bottom right, #b827fc 0%, #2c90fc 25%, #b8fd33 50%, #fec837 75%, #fd1892 100%);
    border-image: -o-linear-gradient(to bottom right, #b827fc 0%, #2c90fc 25%, #b8fd33 50%, #fec837 75%, #fd1892 100%);
	border-image: -moz-linear-gradient(to bottom right, #b827fc 0%, #2c90fc 25%, #b8fd33 50%, #fec837 75%, #fd1892 100%);
    border-image-slice: 1;
}
.ui-layout-pane-west {
    border: 2px solid transparent;
	border-image: linear-gradient(to bottom right, #b827fc 0%, #2c90fc 25%, #b8fd33 50%, #fec837 75%, #fd1892 100%);
    border-image: -webkit-linear-gradient(to bottom right, #b827fc 0%, #2c90fc 25%, #b8fd33 50%, #fec837 75%, #fd1892 100%);
    border-image: -o-linear-gradient(to bottom right, #b827fc 0%, #2c90fc 25%, #b8fd33 50%, #fec837 75%, #fd1892 100%);
	border-image: -moz-linear-gradient(to bottom right, #b827fc 0%, #2c90fc 25%, #b8fd33 50%, #fec837 75%, #fd1892 100%);
    border-image-slice: 1;
}
.ui-layout-resizer {
 	background-color: transparent!important;   
}
.ui-layout-toggler {
    background-color: #232323;
    border: 1px solid transparent;
	border-image: linear-gradient(to bottom right, #b827fc 0%, #2c90fc 25%, #b8fd33 50%, #fec837 75%, #fd1892 100%);
    border-image: -webkit-linear-gradient(to bottom right, #b827fc 0%, #2c90fc 25%, #b8fd33 50%, #fec837 75%, #fd1892 100%);
    border-image: -o-linear-gradient(to bottom right, #b827fc 0%, #2c90fc 25%, #b8fd33 50%, #fec837 75%, #fd1892 100%);
	border-image: -moz-linear-gradient(to bottom right, #b827fc 0%, #2c90fc 25%, #b8fd33 50%, #fec837 75%, #fd1892 100%);
    border-image-slice: 1;
    border-left: none;
}
#header {
    color: #A115A1;
    background-color: #F0F0F0;
    margin-right: 0!important;
    padding: 0!important;
	border: none!important;
    overflow: hidden;
    -webkit-box-shadow: none!important;
    -moz-box-shadow: none!important;
    box-shadow: none!important;
    -webkit-border-top-radius: 0px 0px 0px 0px!important;
 	-moz-border-top-radius: 0px 0px 0px 0px!important;
	border-radius: 0px 0px 0px 0px!important;
}
#content {
 	-webkit-border-bottom-radius: 0px 0px 0px 0px!important;
 	-moz-border-bottom-radius: 0px 0px 0px 0px!important;
	border-radius: 0px 0px 0px 0px!important;   
    border-style: none!important;
    margin: 0px 0px 0px 0px!important;
}
#jump-box {
    background-color: #333333 !important;
    color: white;
    border: 1px solid transparent;
    border-image: linear-gradient(to bottom right, #b827fc 0%, #2c90fc 25%, #b8fd33 50%, #fec837 75%, #fd1892 100%);
    border-image: -webkit-linear-gradient(to bottom right, #b827fc 0%, #2c90fc 25%, #b8fd33 50%, #fec837 75%, #fd1892 100%);
    border-image: -o-linear-gradient(to bottom right, #b827fc 0%, #2c90fc 25%, #b8fd33 50%, #fec837 75%, #fd1892 100%);
	border-image: -moz-linear-gradient(to bottom right, #b827fc 0%, #2c90fc 25%, #b8fd33 50%, #fec837 75%, #fd1892 100%);
    border-image-slice: 1;

}
button {
    background: #282828;
    border: none;
    border: 1px solid transparent;
    border-image: linear-gradient(to bottom right, #b827fc 0%, #2c90fc 25%, #b8fd33 50%, #fec837 75%, #fd1892 100%);
    border-image: -webkit-linear-gradient(to bottom right, #b827fc 0%, #2c90fc 25%, #b8fd33 50%, #fec837 75%, #fd1892 100%);
    border-image: -o-linear-gradient(to bottom right, #b827fc 0%, #2c90fc 25%, #b8fd33 50%, #fec837 75%, #fd1892 100%);
	border-image: -moz-linear-gradient(to bottom right, #b827fc 0%, #2c90fc 25%, #b8fd33 50%, #fec837 75%, #fd1892 100%);
    border-image-slice: 1;
    color: white;
    display: inline-block;
    font-weight: bold;
    font-family: Arial, sans-serif;
    font-size: 12px;
    height: 30px;
    line-height: 30px;
    padding: 0;
    text-shadow: 0 -1px 0 black;
    white-space: nowrap;
    cursor: pointer;
}
.page-title.current {
    background-color: #595959;
    color: white!important;
    border-left: 3px solid #82ed31;
}
.page-title {
    background-color: #595959;
    color: white!important;
}
#nav ul, #nav a {
    color: white !important;
}
}
