css:
@-moz-document url-prefix("https://tm.mania-exchange.com/") {
body {
    background-image: url(https://cdn.discordapp.com/attachments/159365348510924801/321882878499880970/ScreenShot01.jpg) !important;
}
a {
	color: #2d93d8;
}
a:hover {
	color: #62a7d6;
}
a:visited {
	color: #3d6eed;
}

::-webkit-scrollbar {
  width: 13px;
}
/* Track */
::-webkit-scrollbar-track {
    background: #191b1e;
	box-shadow: inset 0 0 4px #0d0e0f;
}
/* Handle */
::-webkit-scrollbar-thumb {
    background: #3f444c; 
}
/* Handle on hover */
::-webkit-scrollbar-thumb:hover {
    background: #515760; 
}

/*
blue: #2582fc
very light: #515760
light: #3f444c
slightly less light: #32363d
dark: #191b1e
very dark: #0d0e0f

text light: #ccd
text less light: #99a
*/
a [class^="icon-"], a [class*=" icon-"] {
    color: #ccd;
}

a:hover [class^="icon-"], a:hover [class*=" icon-"] {
    color: #fff;
}

.userbar {
    background-color: #191b1e;
    border-bottom: 1px solid #0d0e0f;
    color: #ccc;
	box-shadow: 0 1px 3px #000;
}
.userbar-sitetitle-active {
    background-color: #191b1e;
    color: #ccc;
    text-shadow: 1px 1px 1px #0d0e0f;
}
.userbar-sitetitle-active:hover {
    color: #ccc !important;
}
.userbar-button:hover {
    background-color: #3f444c !important;
}
#userbar-sitenav {
	background-color: #191b1e;
	border-left: 1px solid #0d0e0f;
	border-top: none;
	border-right: 1px solid #0d0e0f;
	border-bottom: 1px solid #0d0e0f;
    box-shadow: 0 2px 3px #000;
}
#userbar-sitenav a {
    color: #ccc;
}
#userbar-sitenav a:hover {
    background-color: #3f444c;
}
.userbar-sitenav-splitter {
	background-color: #0d0e0f;
}
#userbar-search {
    border: 1px solid #191b1e;
    background-color: #3f444c;
    box-shadow: 0 0 3px #191b1e inset;
    color: #ccd;
}
#userbar-search::placeholder {
    color: #99a;
}
#userbar-search:focus {
    border: 1px solid #06f;
    box-shadow: 0 0 3px #333 inset, 0 0 9px #27d;
    outline: 0;
}

.container-inner {
    background-color: #191b1e;
    border-radius: 0;
    box-shadow: 0 0 10px #111;
}
#mpxmenu li a {
    background-color: #3f444c;
	background-image: none;
    border-right: 1px solid #191b1e;
    border-top: 1px solid #191b1e;
    border-bottom: 1px solid #191b1e;
    color: #EAFFED;
    box-shadow: none;
}
#mpxmenu li a:hover {
    background-color: #515760;
	background-image: none;
    border-right: 1px solid #191b1e;
    border-top: 1px solid #191b1e;
    border-bottom: 1px solid #191b1e;
    color: #EAFFED;
    box-shadow: none;
}
#mpxmenu li ul li a {
    background: #3f444c;
    color: #fff;
    border-left: solid 1px #0d0e0f;
    border-right: solid 1px #0d0e0f;
    transition: background 0s;
}
#mpxmenu li ul li a:hover {
    background: #515760;
    color: #fff;
    border-left: solid 1px #0d0e0f;
    border-right: solid 1px #0d0e0f;
	border-top: none;
	border-bottom: none;
}
#mpxmenu li ul li:first-child a {
    border-top: solid 1px #0d0e0f;
}
#mpxmenu li ul li:last-child a {
    border-bottom: solid 1px #0d0e0f;
}
.ly-box {
    background-color: #3f444c;
	color: #ccd;
    padding: 7px;
    border-radius: 4px;
    margin-top: 3px;
}
.windowv2-header {
    background: linear-gradient(#5b616b,#515760);
}
.windowv2-header:before, .windowv2-header:after {
    border-color: #33373d transparent transparent transparent;
}
.windowv2-header-focus {
    background: linear-gradient(#2582fc,#2172dd);
    text-shadow: 1px 1px #0a2c59;
}
.windowv2-header-focus:before, .windowv2-header-focus:after {
    border-color: #1956a8 transparent transparent transparent;
}
.windowv2-content {
	background-color: #3f444c;
	color: #ccd;
	border-left: 1px solid #191b1e;
	border-right: 1px solid #191b1e;
	border-bottom: 1px solid #191b1e;
	box-shadow: 0 2px 4.5px #0d0e0f, inset 0 4px 4px -4px #0d0e0f;
}
.windowv2-buttonbar {
    border-top: none;
    background-color: #32363d;
}
.TextQuiet {
    color: #ccd;
}
.WindowHeader1v2 {
    background-color: #32363d;
    color: #ccd;
    border-bottom: none;
    text-shadow: 1px 1px #191b1e;
}
.WindowHeader2v2 {
    background-color: #292c33;
    color: #ccd;
    border-bottom: none;
    text-shadow: 1px 1px #191b1e;
}
.btn {
    color: #ccd !important;
    text-shadow: 0 1px 1px #191b1e;
    background: #3f444c;
    border-color: #191b1e #191b1e #191b1e;
    border-bottom-color: #191b1e;
    border-radius: 4px;
    box-shadow: 0 1px 2px #191b1e;
}
.btn:hover {
    color: #fff;
    text-shadow: 0 1px 1px #191b1e;
    background: #515760;
    border-color: #191b1e #191b1e #191b1e;
    border-bottom-color: #191b1e;
    border-radius: 4px;
    box-shadow: 0 1px 2px #191b1e;
}

.pagination {
    border-radius: 3px;
    border: solid 1px #191b1e;
    background: #3f444c;
    box-shadow: 0px 1px 3px #191b1e;
    color: #ccd;
    text-shadow: 0 1px 0 #191b1e;
}
.pagination.active {
    border-radius: 3px;
    border: solid 1px #191b1e;
    background: #515760;
    box-shadow: 0px 1px 3px #191b1e;
    color: #ccd;
    text-shadow: 0 1px 0 #191b1e;
}
.pagination.disabled {
    opacity: 1;
    border-radius: 3px;
    border: solid 1px #191b1e;
    background: #32363d;
    box-shadow: 0px 1px 3px #191b1e;
    color: #77a;
    text-shadow: 0 1px 0 #191b1e;
}

.windowv2-loading::before {
    content: "";
    display: block;
    background-color: rgb(25, 27, 30, 0.5);
    position: absolute;
    width: 100%;
    height: 100%;
    background-image: url(https://i.imgur.com/TZ77xaf.gif);
    background-position: center;
    background-repeat: no-repeat;
    z-index: 10;
    border-radius: 0 0 2px 2px;
}

.search2-textbox {
	color: #ccd;
    box-shadow: none;
    border: 1px solid #aaa;
    background: #fff;
}
.search2-textbox {
    border: 1px solid #191b1e;
    background-color: #3f444c;
    box-shadow: 0 0 3px #191b1e inset;
    color: #ccd;
}
.search2-textbox::placeholder {
    color: #99a;
}
.search2-textbox:focus {
    border: 1px solid #06f;
    box-shadow: 0 0 3px #333 inset, 0 0 9px #27d;
    outline: 0;
}

.select2-container-multi .select2-choices .select2-search-field {
    
}
.select2-container-multi .select2-choices {
    border: 1px solid #191b1e;
    background: #3f444c;
    box-shadow: 0 0 3px #191b1e inset;
    color: #ccd;
}
.select2-container-multi .select2-choices:focus {
    border: 1px solid #06f;
    box-shadow: 0 0 3px #333 inset, 0 0 9px #27d;
    outline: 0;
}
.select2-container-multi .select2-choices .select2-search-field input {
    color: #ccd;
}
.select2-container-multi .select2-choices .select2-search-choice {
    color: #ccd;
    border: none;
    border-right: 1px solid #191b1e;
	border-radius: 0;
    box-shadow: 0 1px 0 rgba(0, 0, 0, 0.05);
    background: #3f444c;
}

.select2-drop {
    background: #3f444c;
    color: #ccd;
    border: 1px solid #191b1e;
    border-radius: 0;
    box-shadow: 0 4px 5px rgba(0, 0, 0, .25);
}

.select2-results .select2-highlighted {
    background: #2582fc;
}

.select2-search input {
    border: 1px solid #191b1e;
    background: #515760;
    box-shadow: 0 0 3px #191b1e inset;
    color: #ccd;
}
.select2-search input::placeholder {
    color: #99a;
}

.select2-results {
    background: #3f444c;
}

.select2-container .select2-choice {
    border: 1px solid #0d0e0f;
    color: #ccd;
    border-radius: 4px;
    background: #3f444c;
	box-shadow: none;
}
.select2-container .select2-choice .select2-arrow {
    border: none;
    color: #ccd;
    border-radius: 0;
    background: #32363d;
}

.dropdown-menu {
    background-color: #3f444c;
    border: 1px solid #191b1e;
    border-radius: 0;
    box-shadow: 0 4px 5px rgba(0, 0, 0, .25);
}
.dropdown-menu li a {
    color: #ccd;
}
.dropdown-menu li>a:hover, .dropdown-menu li>a:focus, .dropdown-submenu:hover>a {
    background: #515760;
}

.form-niceinput {
    border: 1px solid #191b1e;
    background-color: #3f444c;
    box-shadow: 0 0 3px #191b1e inset;
    color: #ccd;
}
.form-niceinput::placeholder {
    color: #99a;
}
.form-niceinput:focus {
    border: 1px solid #06f;
    box-shadow: 0 0 3px #333 inset, 0 0 9px #27d;
    outline: 0;
}

.WindowHeader1v2-stiketop {
    border-top: 1px solid #191b1e;
}

.dropdown-menu .divider {
    background: none;
    border-bottom: 1px solid #191b1e;
}

table {
    color: #ccd;
}

.btn-active {
	color: #ccd !important;
    text-shadow: 0 1px 1px #191b1e !important;
    background: #D36147 !important;
    border-color: #191b1e #191b1e #191b1e !important;
    border-bottom-color: #191b1e !important;
    border-radius: 4px !important;
    box-shadow: 0 1px 2px #191b1e !important;
}
.btn-active:hover {
    color: #fff !important;
    background: #f96340 !important;
}

.btn-primary2 {
    color: #ccd !important;
    text-shadow: 0 1px 1px #191b1e !important;
    background: #2582fc !important;
    border-color: #191b1e #191b1e #191b1e !important;
    border-bottom-color: #191b1e !important;
    border-radius: 4px !important;
    box-shadow: 0 1px 2px #191b1e !important;
}
.btn-primary2:hover {
    color: #fff !important;
    background: #3f92ff !important;
}

.icon-lock[style="font-size:16px;color:#ffA000"] {
    color: #2582fc !important;
}
.icon-reorder[style="font-size:14px;color:#ffA000"] {
    color: #2582fc !important;
}

.WindowTableCell3v2, .WindowTableCell3v2 td {
    background-color: #3f444c;
    border-bottom: solid 1px #191b1e;
}

#fancybox-overlay {
	opacity: 0.5;
	background-color: #191b1e !important;
}

.notif-head {
    background-color: #191b1e;
    box-shadow: none;
    border-bottom: none;
}
.notif-head-title {
	color: #ccd;
    text-shadow: 1px 1px 2px #0d0e0f;
}
.notif-item {
    border-bottom: 1px solid #191b1e;
    background-color: #3f444c;
    color: #ccd;
}

.forum-message-authorinfo {
    background-color: #3f444c;
}
.forum-message-author {
	border-left: 1px solid #191b1e;
}
.moderatorForumTitle {
	color: #2582fc;
}
.forum-lastEditedBox {
    border: 1px solid #191b1e;
    border-radius: 3px;
    box-shadow: #191b1e 0 1px 2px;
	background: #32363d;
}
blockquote {
    background: #32363d;
    border: 1px solid #191b1e;
    box-shadow: 0 1px 3px #191b1e;
    border-radius: 2px;
}
div.forumcode {
    background: #32363d;
    border: 1px solid #191b1e;
    box-shadow: 0 1px 3px #191b1e;
    border-radius: 2px;
}
div.forumcode pre.prettyprint {
    border: 0;
    border-top: 1px solid #191b1e;
    box-shadow: none;
	background: #292c33;
}
hr.bbCodeHr {
    background-color: #191b1e;
    box-shadow: none;
}
.ForumTrack {
    border: solid 1px #191b1e;
    box-shadow: 0 1px 3px #191b1e;
    background: #32363d;
}
.ForumTrackTitle {
    background: #32363d;
    border-bottom: solid 1px #191b1e;
    box-shadow: none;
}
span[style="color:#ADADAD;"] {
	color: #fff !important;
}
span[style="color:#09F;"] {
	color: #0090ff !important;
}

.editor-preview {
    border: 1px solid #191b1e!important;
    border-radius: 3px;
    box-shadow: 0 1px 10px #191b1e;
}

.Window tbody {
    background-color: #32363d;
}
.Window {
	border: 1px solid #191b1e;
}
.WindowLeft {
    opacity: 0;
}
.WindowRight {
    opacity: 0;
}
.WindowLeft img {
    opacity: 0;
}
.WindowRight img {
    opacity: 0;
}
.WindowTopB {
    border-bottom: solid 1px #191b1e;
}
.WindowTopB img {
    opacity: 0;
}
.WindowBottomB {
    border-top: solid 1px #191b1e;
}
.WindowBottomB img {
    opacity: 0;
}

.WindowTableCell1v2, .WindowTableCell1v2 td {
    background-color: #3f444c !important;
}
.WindowTableCell2v2, .WindowTableCell2v2 td {
    background-color: #4a4f59 !important;
}
.WindowTableCell2v2, .WindowTableCell2v2 td, .WindowTableCell1v2, .WindowTableCell1v2 td {
    color: #ccd !important;
}
tr.WindowTableCell1v2.with-hover:hover td, tr.WindowTableCell2v2.with-hover:hover td {
    color: #fff !important;
    background: #191b1e !important;
}
.coauthor-badge {
    color: #fff !important;
    background-color: #2582fc;
    border-radius: 3px;
	border: none;
    text-shadow: 0px 1px #0d0e0f;
    box-shadow: 0px 1px #0d0e0f;
}

/*
blue: #2582fc
very light: #515760
light: #3f444c
slightly less light: #32363d
dark: #191b1e
very dark: #0d0e0f

text light: #ccd
text less light: #99a
*/
}
