css:
@-moz-document domain(prog101.com) {
    :root {
        --Dark1: #111;
        --Dark2: #333;
        --Dark3: #555;
        --FontFamily: Arial, Verdana, sans-serif;
        --TextColor1: white;
        --RedBrand: #f03;
        --ThemeColor: /*[[ThemeColor]]*/;
        --OrangeBrand: #f50;
    }
    body {
        background-color: var(--Dark1);
        color: var(--TextColor1);
        font-family: var(--FontFamily);
    }
    div.page {
        background-color: var(--Dark2);
        color: var(--TextColor1);
        font-family: var(--FontFamily);
        border: 2px solid var(--ThemeColor);
        border-radius: 10px;
    }
    div.menu {
        background-color: var(--Dark1);
        border-radius: 4px;
    }
    div.basdepage {
        background-color: var(--Dark1);
        border-radius: 4px;
    }

    div.form {
        background-color: var(--Dark1);
        border: 2px solid var(--ThemeColor);
        border-radius: 4px;
    }
  
    .motcle, .fichier {
        background: var(--Dark2);
        border-radius: 2px;
        color: var(--ThemeColor);
        padding: 2px;
    }

    pre.prettyprint {
        padding: 15px;
        border: 2px solid var(--ThemeColor);
        background-color: var(--Dark2);
        border-radius: 4px;
    }

    .pln {
        color: white;
    }

    .clo, .opn, .pun {
        color: #999;
    }

    .kwd {
        color: #f50;
    }

    .com {
        color: #0c0;
    }

    .lit {
        color: #0ff;
    }

    .typ {
        color: #a0f;
    }

    img{
        background-color: white;
    }

    .classe {
        color: #05f
    }

    h1 {
        background-color: var(--Dark2);
        border: 3px solid var(--ThemeColor);
        border-radius: 4px;
        color: var(--ThemeColor);
    }

    .navbar-default {
        background-color: #333;
        border-color: #111;
    }

    .navbar-default .navbar-nav > li > a {
        color: var(--RedBrand);
    }

    .navbar-default .navbar-brand {
        color: var(--RedBrand);
    }

    .well {
        background-color: #333;
        border: 2px solid var(--ThemeColor);
    }

    em {
        color: var(--RedBrand);
    }

    table {
        background-color: var(--Dark1);
    }

    td {
        background-color: var(--Dark2);
        border-radius: 3px;
        border: 2px solid var(--Dark3);
    }

    a:link {
        color: #08f;
    }

    a:visited {
        color: #80f;
    }

    .conge {
        background-color: var(--Dark2);
    }

    h1 > span {
        color: var(--RedBrand) !important;
        padding: 10px;
    }
    .eval {
        color: var(--OrangeBrand);
    }

    .examen {
        color: var(--OrangeBrand);
    }
    .tag {
        color: #50f;
    }

    .atn {
        color: #c0c;
    }

    .atv {
        color: #0f0;
    }

    pre {
        background-color: var(--Dark2);
        border: 2px solid var(--Dark3);
        color: #eee;
    }
}
