css:
@-moz-document url-prefix("https://discordbots.org/servers") {
/* Main dark theme */
article {
    background-color: #2a2d32;
}
.medalSponsor .bot-btns .btn {
    background-color: rgb(153,109,10) !important;
}
h4 {
    color: white !important;
}
.section-title p {
    color: #7f8083 !important;
}
body {
    background-color: #2a2d32;
}
footer {
    background-color: #262626;
    border-color: #222428; 
}

#wrapperrr {
    background-color: #262626;
}
.is-fullhd > .exploremorebots .tagbutton {
    border-bottom: solid 1px;
}
.tagbutton {
    color: white !important;
    background-color: #2a2d32 !important;
}
.tagbutton:hover {
    background-color: #7289DA !important;
    border-color: #7289DA !important;
}
html {
   background-color: #2a2d32;
}
.mainsearch {
    background-color: white !important;
}
}

@-moz-document url-prefix("https://discordbots.org/servers") {
/* Dark bot cards */
.bot-card .content {
    box-shadow: 0px 0px 8px -2px rgba(0,0,0,0.6) !important;
    color: #687dc6;
    background-color: rgba(39, 41, 46,0.7) !important
}
.bot-card div .bot-btns .btn:hover {
    background-color: #7289da;
}
.bot-card:hover .content {
    box-shadow: 0px 4px 8px -2px rgba(0,0,0,0.6) !important;
    background-color: #24262b !important;
    opacity: 1 !important;
}
.bot-card .content .guildCardSplash .image::before {
    opacity: 0.1 !important;
}
.bot-card .content .guildCardSplash {
    opacity: 0.1 !important;
}
.bot-card .content .guildCardSplash .image {
    opacity: 0.5 !important;
}

.bot-card:hover .content .guildCardSplash  {
    opacity: 0.3 !important;
}
.bot-card .content.hasSplash {
    
    background-color: rgba(39, 41, 46, 1) !important
}
.dslPartnered .bot-name {
    color: #eac605;
}
.dslPartnered .bot-description {
    color: white;
}

.bot-card .medalSponsor .bot-description {
    color: white !important;
}
.medalSponsor .bot-name {
    color: #eac605 !important;
}

.bot-name {
    color: white;
}
.bot-name:hover {
    color: #334dad;
}
.bot-description {
    color: rgba(255,255,255,0.7);
}
.bot-card div .bot-btns .btn {
    color: #6a7ecc !important;
}

.columns .bot-img {
    box-shadow: none !important;
}
}

@-moz-document url-prefix("https://discordbots.org/servers/new") {
/* Dark theme for user profile */
.maincon .content {
    color: lightgray !important;
    background-color: #282a2f;
}
.content h1 {
    color: white;
}
}

@-moz-document url-prefix("https://discordbots.org/servers/") {
/* Dark theme for server pages */
.botpagebutton {
    color: lightgray;
    background-color: #444444;
}
.page .container {
    border: none !important;
}
.longdescription .content {
    color: lightgray;
    background-color: #222426;
}
.blog_card {
    opacity: 0.9 !important;
    color: lightgray;
    background-color: #5c5c5c !important;
}
#createdby {
    color: grey;
}
.blog_card .details b {
    color: rgb(217,217,217);
}
.blog_card:hover::after {
     background-color: #3c3b3b !important;
}
.atag {
    color: black;
    background-color: darkgrey;
}
.atag:hover {
    color: black;
}


/* Edit page css */
.maincon .content {
    color: lightgrey;
    background-color: #222426;
}
.label {
   color: lightgrey; 
}
.field h2 {
    color: white;
}
input {
    color: white !important;
    border: 1px solid #2a2d32 !important;
    background-color: #34363a !important;
}
.maincon .button {
    background-color: #7289da !important;
}
.checkmark {
    background-color: grey;
}
code {
    background-color: lightgrey;
}
.suggestedtags {
     background-color: rgb(81,81,81);
}
.suggestedtags p:hover {
    background-color: lightgrey;
}
}
