@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document 
domain("hattrick.ws"),
domain("hattrick.interia.pl"),
domain("hattrick.uol.com.br"),
domain("hattrick.org") {

/* LOGO HATTRICK */
#logo {
	background: transparent url(http://nrgjack.altervista.org/_altervista_ht/style/fatalitrick/fatalitrick.png)  no-repeat scroll left top !important;
	height:27px!important;
	top:-5px!important;
	left:2px!important;
}


/* SFONDO HATTRICK */
 body{
	background: url(http://nrgjack.altervista.org/_altervista_ht/style/blacktrick/bg.png)   !important;
}


/* TEAMLINKS GROSSI SENZA UNDERLINE */
#teamLinks a{
	text-decoration:none!important;
	font-weight:bold!important;
	font-size:larger!important;
	color:black!important;
}
#teamLinks a:hover{
	color:green!important;
}

/* MENU HATTRICK BASE CHIARA */
#menu {
	background: green none  no-repeat scroll left top !important;
	-moz-border-radius: 7px;
	-webkit-border-radius: 7px;
	border: 1px solid #000;
	height:30px!important;

}

#menu a
{
	line-height: 30px !important; 
	color:black!important; 
}


/* MENU HATTRICK BASE SCURA AL PASSAGGIO MOUSE */
#menu a:hover{
	background: black !important;
	color:#00FF00!important;
}

/* BOX PARTE ALTA BORDI ARROTONDATI*/
.sidebarBox .boxHead h2, 
.subMenuBox .boxHead h2, 
.main .boxHead h2,
.sidebarBox .boxHead .boxLeft h2,
.subMenuBox .boxHead .boxLeft h2,
.main .boxHead .boxLeft h2{
	background: #00FF00 none  no-repeat scroll left top !important;
	-moz-border-radius-topleft : 7px !important;
	-webkit-border-top-left-radius : 7px !important;
	-moz-border-radius-topright : 7px !important;
	-webkit-border-top-right-radius : 7px !important;
	border: 1px solid black !important;
	margin-left:0px !important;
	color:black!important; 
}

/* SCRITTA PARTE CENTRALE BORDI ARROTONDATI*/
.main .boxHead h2 span, .main .boxHead h2 a {
	color:#006600!important; 
}

/* BOX PARTE ALTA BORDI ARROTONDATI*/
.sidebarBox .boxHead , 
.subMenuBox .boxHead , 
.main .boxHead ,
.sidebarBox .boxHead .boxLeft,
.subMenuBox .boxHead .boxLeft,
.main .boxHead .boxLeft{
	background: transparent none  no-repeat scroll left top !important;
}

/* BOX PARTE CENTRALE BORDI*/
.sidebarBox .boxBody, 
.subMenuBox .boxBody, 
.main .boxBody {
	background:#FFFFFF none repeat scroll 0 0!important;
	border-left:1px solid  black !important;
	border-right:1px solid  black !important;
}

/* BOX PARTE BASSA BORDI ARROTONDATI*/
.sidebarBox .boxFooter .boxLeft, 
.subMenuBox .boxFooter .boxLeft, 
.main .boxFooter .boxLeft {
	background: #222 none  no-repeat scroll left top !important;
	-moz-border-radius-bottomleft : 7px !important;
	-webkit-border-bottom-left-radius : 7px !important;
	-moz-border-radius-bottomright : 7px !important;
	-webkit-border-bottom-right-radius : 7px !important;
	border: 1px solid black !important;
}

/* PAGINA BORDI ARROTONDATI*/
#page{
	border: 1px solid black !important;
	-moz-border-radius-topleft : 7px !important;
	-webkit-border-top-left-radius : 7px !important;
	-moz-border-radius-topright : 7px !important;
	-webkit-border-top-right-radius : 7px !important;
	-moz-border-radius-bottomleft : 7px !important;
	-webkit-border-bottom-left-radius : 7px !important;
	-moz-border-radius-bottomright : 7px !important;
	-webkit-border-bottom-right-radius : 7px !important;
}
/* Header e bottom BORDI ARROTONDATI*/
#top{
	-moz-border-radius-topleft : 7px !important;
	-webkit-border-top-left-radius : 7px !important;
	-moz-border-radius-topright : 7px !important;
	-webkit-border-top-right-radius : 7px !important;
}
#bottom{
	-moz-border-radius-bottomleft : 7px !important;
	-webkit-border-bottom-left-radius : 7px !important;
	-moz-border-radius-bottomright : 7px !important;
	-webkit-border-bottom-right-radius : 7px !important;
}

/* PAGINA COLORE SFONDO CENTRALE*/
#top, #header, #content, #bottom, #page{
	background: #99FF99 none  no-repeat scroll left top !important;
}


/* FORUM BOX MESSAGGI */
.cfWrapper {
	border: 1px solid #333  !important;
}
.cfHeader {
	background: #333  !important;
	color: #fff  !important;
}
.cfHeader a{
	color: #fff  !important;
}

/* TUTTE LE IMMAGINI */
img.scLive, 
img.scTransfer, 
img.scMail, 
img.scNewMail, 
img.scNotes, 
img.scSearch, 
img.scMySupporters, 
img.scReminders, 
img.scBookmarks, 
img.scDigit1, 
img.scDigit2, 
img.scDigit3, 
img.scDigit4,
img.scDigit5, 
img.scChallenges, 
img.scManager, 
img.scMatches, 
img.scPlayers, 
img.scBids, 
img.scFans, 
img.scTraining, 
img.scMatches2, 
img.scEconomy, 
img.f_quote1, 
img.f_quote2, 
img.f_bold, 
img.f_italic, 
img.f_ul, 
img.f_hr, 
img.f_player, 
.bookmarkSmall img, 
img.f_team, 
img.f_match, 
img.f_fed, 
img.f_message, 
img.f_series, 
img.f_www, 
img.hotlist, 
img.matchOrder, 
img.matchOrderSet, 
img.matchOrderSetDisabled, 
img.matchOrderWrite, 
img.matchOrderWriteDisabled, 
img.matchHTLive, 
img.matchFriendly,
img.matchLeague, 
img.matchCup, 
img.matchQualification, 
img.matchMasters, 
img.supporterRed,
img.supporterGreen {

	background-image: url(http://nrgjack.altervista.org/_altervista_ht/style/blacktrick/master_icons.png)   !important;

}




}
