@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("netflix.com") {
/*[[color]]*/
.svg-nfLogo,
.pinning-header .main-header .logo,
#hd .logo {
    color: var(--logo-c) !important;
    fill: var(--logo-c) !important;
}
.nf-loading-spinner {
    background-image: var(--spinner);
}
.akira-spinner {
	color: var(--spinner-c);
}
.nfp .scrubber-container .current-progress,
.nfp .volume-controller .slider-bar-percentage {
    background: var(--scrubber-c-1) !important;
}
.nfp .scrubber-container .scrubber-head,
.nfp .volume-controller .scrubber-head {
    background: var(--scrubber-c-2) !important;
    border: 1px solid var(--scrubber-border-color) !important;
}
.progress-completed {
    background-color: var(--progress-completed-c) !important;
}
.Progress.Progress--modern::-webkit-progress-value {
    background: var(--progress-completed-c) !important;
}
.Progress.Progress--modern::-moz-progress-bar {
    background: var(--progress-completed-c) !important;
}
.bob-card-adult-video-merch .bob-play-hitzone:hover ~ .bob-overview-wrapper .svg-icon-play-with-ring {
    color: var(--play-icon-c) !important;
}
.episodeLockup .episodePlay .play {
    color: var(--play-icon-c) !important;
}
.episodeLockup .episodePlay:focus .play,
.episodeLockup .episodePlay:hover .play,
.simsLockup .simsPlay:focus .play,
.simsLockup .simsPlay:hover .play,
.simsLockup .trailerPlay:focus .play,
.simsLockup .trailerPlay:hover .play,
.trailerLockup .simsPlay:focus .play,
.trailerLockup .simsPlay:hover .play,
.trailerLockup .trailerPlay:focus .play,
.trailerLockup .trailerPlay:hover .play,
.bob-card-adult-tall-panel-video-merch .bob-play-hitzone:hover ~ .bob-overview-wrapper .svg-icon-play-with-ring,
.title-card .title-card-play:focus .play,
.title-card .title-card-play:hover .play {
    color: var(--play-icon-c) !important;
}
.nfHeader .authLinks,
.hero-cta-btn-txt,
.tin-module .cta-link-wrapper .cta-form .btn,
button.btn,
.nf-flat-button-primary > span {
    color: var(--btn-txt-c);
}
.bob-card-adult-tall-panel-video-merch .bob-jaw-hitzone:hover ~ .bob-chevron-wrapper {
    color: var(--play-icon-c) !important;
    stroke: var(--play-icon-c) !important;
}
.WatchNext-still-hover-container .PlayIcon {
    background-image: var(--play-icon) !important;
    background-size: 40px 50px !important;
    background-position: 18px 10px;
    background-repeat: no-repeat;
    box-sizing: border-box;
    display: block;
    width: 80px;
    height: 80px;
    padding-top: 14px;
    padding-left: 8px;
    line-height: 20px;
    border: 6px solid #fff;
    border-radius: 50%;
    color: #f5f5f5;
    text-align: center;
    text-decoration: none;
    background-color: rgba(0, 0, 0, 0.5);
    font-size: 20px;
    font-weight: bold;
    transition: all 0.3s ease;
}
.WatchNext-still-hover-container .PlayIcon:hover {
    background-color: rgba(0, 0, 0, 0.8);
    box-shadow: 0px 0px 10px rgba(255, 255, 255, 1);
    text-shadow: 0px 0px 10px rgba(255, 255, 255, 1);
}
.bob-card-adult-video-merch .bob-jaw-hitzone:hover ~ .bob-chevron-wrapper {
    color: var(--down-arrow-c) !important;
    stroke: var(--down-arrow-c) !important;
}
.nf-flat-button-primary {
    background-color: var(--default-btn-c) !important;
    border-color: var(--default-btn-border-c) !important;
}
.nf-flat-button-primary:hover {
    background-color: var(--default-btn-hover-c) !important;
}
.nf-flat-button {
    background-color: var(--default-btn-c) !important;
    border-color: var(--default-btn-border-c) !important;
}
.WatchNext-autoplay .countdown-container > strong {
    color: var(--countdown-timer-c) !important;
}
.jawBoneContainer .menu li.current span {
    background-color: var(--menu-select-border-bottom-c) !important;
}
.tags-separator {
    background-color: var(--tags-separator) !important;
}
.nfHeader .authLinks:not(header-login-link) {
    background: transparent !important;
    border-color: transparent !important;
    color: #000000 !important;
    -webkit-box-shadow: 0 0 0 0 !important;
    -moz-box-shadow: 0 0 0 0 !important;
    box-shadow: 0 0 0 0 !important;
}
.nfHeader .authLinks:not(header-login-link):hover {
    background: transparent !important;
    border-color: transparent !important;
    color: #000000 !important;
    -webkit-box-shadow: 0 0 0 0 !important;
    -moz-box-shadow: 0 0 0 0 !important;
    box-shadow: 0 0 0 0 !important;
}
#btn-save,
#btn-cancel,
.btn,
.btn-submit,
.btn-small,
.btn-plain,
.btn-medium,
.btn-red,
.btn-blue,
.account-cancel-button,
.cancel-password-button,
.cancel-save-button,
.hybrid-login-form .login-button,
.nfHeader .authLinks,
.action-button,
.nf-flat-button,
.profile-button.preferred-action a,
.profile-button.preferred-action span,
.authLinks.redButton,
.nf-btn-solid.nf-btn-primary,
.nf-btn-twotone.nf-btn-primary,
.ReportAProblemDialog--submit-button,
.ReportAProblemDialog--finish-button {
    background: var(--default-btn-c) !important;
    border-color: var(--default-btn-border-c) !important;
    color: var(--btn-txt-c) !important;
    -webkit-box-shadow: 0 1px 0 rgba(0, 0, 0, .45) !important;
    -moz-box-shadow: 0 1px 0 rgba(0, 0, 0, .45) !important;
    box-shadow: 0 1px 0 rgba(0, 0, 0, .45) !important;
}
#btn-save:hover,
#btn-save:focus,
#btn-save:active,
#btn-cancel:hover,
#btn-cancel:focus,
#btn-cancel:active,
.btn:hover,
.btn:focus,
.btn:active,
.btn-submit:hover,
.btn-submit:focus,
.btn-submit:active,
.btn-small:hover,
.btn-small:focus,
.btn-small:active,
.btn-plain:hover,
.btn-plain:focus,
.btn-plain:active,
.btn-medium:hover,
.btn-medium:focus,
.btn-medium:active,
.btn-red.hover,
.btn-red:focus,
.btn-red:hover,
.btn-blue:hover,
.btn-blue:focus,
.btn-blue:active,
.account-cancel-button:hover,
.account-cancel-button:focus,
.account-cancel-button:active,
.cancel-password-button:hover,
.cancel-password-button:focus,
.cancel-password-button:active,
.cancel-save-button:hover,
.cancel-save-button:focus,
.cancel-save-button:active,
.hybrid-login-form .login-button:hover,
.hybrid-login-form .login-button:focus,
.hybrid-login-form .login-button:active,
.nfHeader .authLinks:hover,
.nfHeader .authLinks:focus,
.nfHeader .authLinks:active,
.action-button:hover,
.action-button:focus,
.action-button:active,
.nf-flat-button:hover,
.nf-flat-button:focus,
.nf-flat-button:active,
.profile-button.preferred-action:hover a,
.profile-button.preferred-action:hover span,
.profile-button.preferred-action:focus a,
.profile-button.preferred-action:focus span,
.profile-button.preferred-action:active a,
.profile-button.preferred-action:active span,
.authLinks.redButton:hover,
.authLinks.redButton:focus,
.authLinks.redButton:active,
.nf-btn-solid.nf-btn-primary:hover,
.nf-btn-solid.nf-btn-primary:focus,
.nf-btn-solid.nf-btn-primary:active,
.nf-btn-solid.nf-btn-primary.hover,
.nf-btn-twotone.nf-btn-primary:hover,
.nf-btn-twotone.nf-btn-primary:focus,
.nf-btn-twotone.nf-btn-primary:active,
.nf-btn-twotone.nf-btn-primary.hover,
.ReportAProblemDialog--submit-button:hover,
.ReportAProblemDialog--submit-button:focus,
.ReportAProblemDialog--submit-button:active,
.ReportAProblemDialog--finish-button:hover,
.ReportAProblemDialog--finish-button:focus,
.ReportAProblemDialog--finish-button:active {
    background: var(--default-btn-hover-c) !important;
    border-color: var(--default-btn-border-hover-c) !important;
    color: var(--btn-txt-c) !important;
    -webkit-box-shadow: 0 1px 0 rgba(0, 0, 0, .45) !important;
    -moz-box-shadow: 0 1px 0 rgba(0, 0, 0, .45) !important;
    box-shadow: 0 1px 0 rgba(0, 0, 0, .45) !important;
}
button.medium,
.ltr-14a0la9.opacity-60 {
    background: var(--default-btn-c) !important;
}
button:hover.medium:hover,
.ltr-14a0la9.opacity-60:hover {
    background: var(--default-btn-hover-c) !important;
}
.ltr-6gmfhw,
span.ltr-18i00qw {
    color: var(--btn-txt-c) !important;
}
