}
.ProfileWarning--action-button-refresh {
    color: var(--btn-txt-c) !important;
    background-color: var(--default-btn-c) !important;
    border: 1px solid var(--btn-border-c) !important;
}
.ProfileWarning--action-button-refresh:hover {
    color: var(--btn-txt-c) !important;
    background-color: var(--default-btn-hover-c) !important;
    border: 1px solid var(--btn-border-c) !important;
}
.ltr-2blbv3,
.ltr-tzf3pd {
    color: var(--btn-txt-c) !important;
    background-color: var(--default-btn-c) !important;
}
.ltr-2blbv3:hover,
.ltr-tzf3pd:hover {
    color: var(--btn-txt-c) !important;
    background-color: var(--default-btn-hover-c) !important;
}
.btn-draining {
    background-color: var(--default-btn-c) !important;
    color: var(--btn-txt-c) !important;
}
.btn-draining .draining {
    -webkit-mask-image: -webkit-radial-gradient(var(--btn-draining-color), var(--btn-draining-color)) !important;
    color: var(--btn-txt-c) !important;
}
.btn-draining .draining .inner {
    background-color: var(--btn-draining-color) !important;
    color: var(--btn-txt-c) !important;
}
.advisory-bar {
    background: var(--default-border-c) !important;
}
.notification-pill {
    background-color: var(--notification-pill) !important;
    color: var(--btn-txt-c) !important;
}
