    background-color: #141414 !important;
    border: 2px solid var(--default-border-c);
    -webkit-box-shadow: 0 0 0 0 !important;
    -moz-box-shadow: 0 0 0 0 !important;
    box-shadow: 0 0 0 0 !important;
    color: white !important;
}
.ReportAProblemDialog--checkbox-input:checked + .ReportAProblemDialog--checkbox::before {
	color: var(--checkmark-color) !important;
	content: 'âœ”' !important;
}
.ReportAProblemDialog--checkbox, .ReportAProblemDialog--textarea {
	border: 1px solid var(--default-border-c) !important;
}
