}
.ReportAProblemDialog--dialog-box {
    background-color: #141414 !important;
    border: 2px solid var(--default-border-c) !important;
    -webkit-box-shadow: 0 0 0 0 !important;
    -moz-box-shadow: 0 0 0 0 !important;
    box-shadow: 0 0 0 0 !important;
    color: #ffffff!important;
}
.hybrid-login-form .nfEmailPhoneInError .nfEmailPhoneControls::after,
.hybrid-login-form .nfPasswordInError .nfPasswordControls::after {
    border-bottom: 2px solid var(--default-border-c) !important;
}
.hybrid-login-form .inputError {
    color: var(--text-color-a) !important;
}
.hybrid-login-form .login-remember-me input[type=checkbox] + label::before {
    background: #0d0d0d !important;
    border: 1px solid var(--default-border-c) !important;
}
.hybrid-login-form .login-remember-me input[type=checkbox]:checked + label::after {
    color: var(--default-border-c) !important
}
.sub-menu.theme-lakira > .topbar {
    background: var(--default-border-c) !important;
}
.notifications .callout-arrow {
