}
.selected-indicator {
    background: var(--default-btn-c) !important;
    background-image: none !important;
}
.selected-indicator::after {
    background: url("data:image/svg+xml;base64,") !important;
    background-repeat: no-repeat !important;
    background-size: 25px 25px !important;
    content: ' ' !important;
    display: block !important;
    width: 35px !important;
    height: 35px !important;
    overflow: hidden !important;
    clear: both !important;
    position: relative !important;
    top: -10px !important;
    left: 8px !important;
    bottom: 0 !important;
    right: 0 !important;
}
.stacked-large-selection-list-item.selected,
.stacked-large-selection-list-item-disabled.selected {
