.fb-minimal .fb-login:hover {
    background: var(--default-btn-hover-c) !important;
    padding: 5px !important;
    color: var(--btn-txt-c) !important;
}
.hybrid-login-form .facebookForm .fbBtnText {
    color: var(--btn-txt-c) !important;
    font-size: 13px;
    font-weight: 500;
}
