#nfplayerBackTen > g > polyline,
#nfplayerFastForward > g > g > path,
#nfplayerBackTen > g,
#nfplayerFastForward > g,
#nfplayerFastForward > g > g > polyline {
    stroke: var(--default-btn-c) !important;
}
