css:
@-moz-document domain("mediafire.com") {
.page, .nonDLContentWrap {
    background: url("https://i.ibb.co/9ZFpBqM/Blur-of-Light.jpg") no-repeat center center fixed;
    -webkit-background-size: cover;
    -moz-background-size: cover;
    -o-background-size: cover;
    background-size: cover;
}
.download_link a, .download_link input, .download_link button, #authorize_dl_btn {
    display: block;
    border-radius: 35px 0px 35px 0px;
    text-decoration: none;
    color: #fff;
    background-color: #a0bf17;
    font-weight: 600;
    font-size: 16px;
    text-transform: uppercase;
    border: none;
    -webkit-appearance: none;
    outline: none;
    cursor: pointer;
    opacity: 0.9;
    transition: 0.2s;
}
.download_link a:hover, .download_link input:hover, .download_link button:hover, #authorize_dl_btn:hover {
    opacity: 1;
    box-shadow: 0px 0 19px 2px #7a9017
}
.center {
    width: 1020px;
    height: 490px;
    border-radius: 50px 0px 50px 0px;
    border: 0px solid #000000;
    box-shadow: 0px 0 19px 0px #292929;
}
.ads .dl-btn-cont {
    width: auto;
    border-radius: 6px 6px 6px 6px;
    margin: 0;
    border-radius: 0px;
    border-color: black;
}
.sidebar {
    background: #ffffff;
    border-radius: 30px 0px 30px 0px;
}
.sidebar, .sidebar>div {
    background: #292929 !important;
    color: #fff;
    margin-bottom: 10px;
    border-radius: 9px 0px 0px 9px;
}
a {
    text-decoration: none;
    color: #53f512;
}
.dl-promo-cont, .header, .social-fb-cont, .DLExtraInfo, #footer {
    display: none;
}
}
