css:
@-moz-document domain(trwalamotywacja.pl)
{
body {
    font-family: Helvetica Neue,Helvetica,Arial,sans-serif;
    font-size: 14px;
    line-height: 1.42857143;
    color: #dcdcdc;
    background-color: #252525;
}

div.resources-container {
    color: #dcdcdc;
    background-color: #252525;
}
#app-container #content div[ui-view]>* {
    background-color: #252525;
}
div.board.middle div.content .goal-list {
    background-color: #252525;
    color: #dcdcdc;
}
.goal.selected, .mark-day.selected {
    background-color: #45484a;
}
#techniques-panel .techniques .techniques-wrapping {
    background-color: #252525;
    color: #dcdcdc;
}
#techniques-panel .techniques div.technique p {
    color: #dcdcdc;
}
#app-container #content .middle div.content {
    background-color: #252525;
    color: #dcdcdc;
}
.knowledge-part-list .knowledge-part-info .title {
    color: #dadada;
}
.knowledge-part-list .gray, .presentation-view .gray {
    color: #dcdcdc;
}
navigation #navigation ul li.disabled {
    color: #dcdcdc;
}
navigation #navigation {
    background-color: hsla(0, 0%, 12%, 1);
    color: #dcdcdc;
}
navigation #navigation ul li.active {
    background-color: #45484a;
}
div.middle.prby-end p {
    color: #dcdcdc;
}
div.middle.prby-end div.content div.condition h2 {
    color: #dcdcdc;
}
.presentation-view .task-container {
    color: #dcdcdc;
}
.tool-container .title-container {
    color: #dcdcdc;
}
a.button.blue-border, button.button.blue-border {
    color: #3498db;
    border: 2px solid #3498db;
    background-color: #252525;
}
div.accordion div.accordion-answer {
    color: #dcdcdc;
}
div.accordion div.accordion-header {
    background-color: hsla(0, 0%, 10%, 1);
    color: #dcdcdc;
}
.tool-container>.best-you-challenge .present-me-container .present-me p {
    color: #dcdcdc;
}
.development-plan .content .title-container span.title {
    color: #dcdcdc;
    display: block;
}
.development-plan .content .title-container .on-off-info {
    font-size: 18px;
    color: #dcdcdc;
    display: block;
}
.development-plan .content .task-container, .development-plan .content .timer {
    margin-top: 10px;
    font-size: 20px;
    color: #dcdcdc;
}
.settings h2 {
    font-size: 1.5em;
    color: #dcdcdc;
    text-align: left;
    margin: 0;
}
.settings .form-container p {
    color: #dcdcdc;
}
.settings .name {
    color: #dcdcdc;
    font-size: 1.2em;
}
.form-container label {
    color: #dcdcdc;
    font-weight: 400;
}
.settings .information-container .footer span {
    font-size: .7em;
    color: #dcdcdc;
    display: inline-block;
}
/*.form-container form input.ng-invalid.ng-pristine {
    filter: invert(70%) contrast(2);
}*/
.form-container input, .form-container select, .form-container textarea {
    font-size: 1em;
    color: #dcdcdc;
    padding-left: 10px;
    background-color: hsla(0, 0%, 10%, 1);
    border: 1px solid silver;
}
label.select select {
    color: #dcdcdc;
    border: 1px solid silver;
}


div.middle.motivation-tree div.context div.context-title {
    background-color: hsla(0, 0%, 10%, 1);
    padding: 10px 20px;
}
div.item-list div.item {
    margin: 0;
    padding: 7px 0;
    border-bottom: 2px solid #ededed;
}
.goal.selected, .mark-day.selected {
    background-color: hsla(204, 0%, 20%, 1);
}
div.editable-item textarea {
    padding: .4em;
    padding-left: 1.5em;
    min-height: 1em;
    flex: 1;
    font-size: 1.2em;
    resize: none;
    background: #252525;
    color: #dcdcdc;
    border: 2px solid #e1e1e1;
}
div.editable-item textarea:focus {
  
    background: #4d86ca;
    
}
div.accordion div.accordion-header:hover {
    background-color: #4d86ca;
}
#techniques-panel .techniques {
    background: #252525;

}
div.calendar div.day:not(.empty):not(.completed):not(.failed):not(.emergency):not(.skipped):not(.decision):not(.finish) div.circle {
    background: #5f5f5f;
    color: #999;
}
.recommendations .content h2 {
    text-align: center;
    color: #dcdcdc;
    font-size: 30px;
    margin: 20px 0;
}
.recommendations .content h6 {
    text-align: center;
    color: #dcdcdc;
    font-size: 16px;
    margin: 10px 0;
    margin-top: 0;
}
.recommendations .content h4 {
    text-align: center;
    color: #dcdcdc;
    font-size: 20px;
    margin: 10px 0;
}
.item-note .note-bar {
    color: #dcdcdc;
    background-color: #383737;

}
}
