css:
@-webkit-document url-prefix('http://baidu.com'),
url-prefix('https://baidu.com'), 
url-prefix('http://www.baidu.com'), 
url-prefix('https://www.baidu.com') {
	#container > div.head_nums_cont_outer.OP_LOG > div {
		top: 0px !important;
	}
	#s_tab > div > a:nth-child(10) {
		display: none !important;
	}
	#container
		> div.head_nums_cont_outer.OP_LOG
		> div
		> div.search_tool_conter
		span {
		text-align: center !important;
		font-size: 120% !important;
		color: #989a99 !important;
		margin: 0px 20px !important;
		border-radius: 5px;
		padding: 6px 10px !important;
		box-shadow: 0px 12px 20px 1px #ccc;
	}
	#container
		> div.head_nums_cont_outer.OP_LOG
		> div
		> div.search_tool_conter
		span:hover {
		font-weight: bold;
		transform: translateY(0.1px);
	}
	#s_tab > div > a:nth-child(2) {
		display: none !important;
	}
	#s_tab > div > a:nth-child(6) {
		display: none !important;
	}
	#s_tab > div > a:nth-child(8) {
		display: none !important;
	}
	#container
		> div.head_nums_cont_outer.OP_LOG
		> div
		> div.search_tool_conter
		> span.c-gap-left-samll.search_tool_close {
		display: none !important;
	}
	#container .head_nums_cont_outer,
	.hint_common_restop {
		position: absolute !important;
		left: 10% !important;
		width: 80% !important;
		margin-top: -46px !important;
		margin-left: 520px !important;
	}
	#container > div.hint_common_restop {
		display: none !important;
	}
	#foot {
		display: none !important;
	}
	#head.s_down {
		box-shadow: 0 0 5px #888 !important;
	}
	/**计数器的颜色样式*/
	.CounterT {
		background: #fd9999;
	}
	/**右侧栏的样式-其实不开启更好看一些*/
	#content_right {
		padding: 20px 15px 15px;
		border-radius: 5px;
		background-color: #fff;
		box-sizing: border-box;
		box-shadow: 0 0 20px 2px rgba(0, 0, 0, 0.1);
		-webkit-box-shadow: 0 0 20px 2px rgba(0, 0, 0, 0.1);
		-moz-box-shadow: 0 0 20px 2px rgba(0, 0, 0, 0.1);
	}
	#content_left {
		margin-top: 20px !important;
	}
	#myuser > li {
		border-radius: 3px !important;
	}
	.c-border {
		margin-left: -18px !important;
	}
	#rs > div {
		transform: scale(0.8) !important;
		float: left;
		margin: none;
	}

	#wrapper #rs {
		margin: -20px auto 20px !important;
	}

	#myuser .myuserconfig {
		transform: scale(0.6) !important;
	}

	.c-tip-con {
		top: 133px !important;
		border-radius: 5px;
		padding: 6px 10px !important;
		box-shadow: -1px 1px 9px 0px #999;
		transition: all 0.8s;
	}
}
