css:
@-moz-document domain("streamdecker.com") {
body{
    background-color: #3c3c3c;
}

.tab-holder[heading="Stats"]{
    background-color: #000;
}
    
.popover{
    background-color: #575757;
}

.loader-box{
    background: #575757 !important;
    box-shadow: none !important;
    filter: drop-shadow(2px 2px 3px #fff) !important;
}

.arrow:after{
    border-right-color: #575757 !important;
}

.btn-default{
    background: #3c3c3c;
}

.btn-default:hover{
    color: #ccc;
    background: #575757;
}

.nav-tabs>li>a:hover{
    background: #575757;
}
.deck-thumb-box{
    background: #3c3c3c !important;
    box-shadow: none !important;
    filter: drop-shadow(2px 2px 3px #fff) !important;
}

p,
.grey{
    color: #ccc;
}

a{
    color: #609dd2;
}

a:hover{
    color: #9cc2e3;
}

.switch{
    border: 1px solid #7d7d7d;
}

body,
.col-md-4,
.btn-default,
.user-profile,
.fa,
.col-xs-8,
.col-xs-4,
.center,
.deck-name,
.card-name,
.card-price,
.card-qty,
.deck-list-buttons .switch,
.type-container > h5,
.col-md-12 > h5,
.loader-box > h4{
    color: #fff;
}
}
