css:
@-moz-document url-prefix("https://void.to/"), url-prefix("https://void.to/index.php") {
/* Navbar */
.bubble {
    padding: 2px 5px;
    background: #202020;
    border-bottom: 1px solid #e11a80;
    border-radius: 2px;
}

/* Navigation Trail Bar */
.breadcrumb li a::before {
    border-left: 30px solid #e11a80;
}
.breadcrumb-wrap {
    border-bottom: 3px #e11a80 solid;
    height: 38px;
}

/* Memebox customising */
.left, .right {
    padding: 0px 20px;
}
#shoutbox .mod:nth-of-type(2) {
    margin-right: 5px;
    border-left: solid 1px #e11a80;
}
#shoutbox .entry.unread .info:before {
    height: 5px;
    width: 5px;
    content: '';
    background: #e11a80;
    border-radius: 0px;
}
#shoutbox input.text {
    border-bottom: 2px solid #e11a80;
}


/* Top Container Border */
.pagination a, .pagination a:hover, .pagination .pagination_current, .container-back {
    border-color: #e11a80;
}

#content .wrapper::before, #content .wrapper::after {
    background-color: #e11a80;
}
#content .wrapper {
    border-top: 3px solid #e11a80;
}

/* Sub-forum Tabs */
.forum-tab-active, .forum-tab-inactive:hover, .thead, .thread-title, .pagination .pagination_current, #mobile_member, button:hover, input.button:hover, a.button:hover, .scrollToDown, .scrollToTop, .useroptions a i, #iconbouton, #menu-deroulant li:hover ul li a, #colorpicker {
    background-image: linear-gradient( to right, #e11a80,
                                                 #202020,
                                                 #202020    );
}

/* Sub-forum Icons */
.nav_link, fieldset.trow2 legend, .headerlist ul, .useroptions a:hover, .thread_status.dot_folder:before, .thread_status.dot_hotfolder:before, .thread_status.dot_hotlockfolder:before, .thread_status.dot_lockfolder:before, .thread_status.dot_newfolder:before, .thread_status.dot_newhotfolder:before, .thread_status.dot_newhotlockfolder:before, .thread_status.dot_newlockfolder:before, .thread_status.folder:before, .thread_status.hotfolder:before, .thread_status.hotlockfolder:before, .thread_status.lockfolder:before, .thread_status.newfolder:before, .thread_status.newhotfolder:before, .thread_status.newhotlockfolder:before, .thread_status.newlockfolder:before, .modcp_nav_item:before, ul.thread_tools, ul.thread_tools li, .wo_title, #menu-deroulant li a:hover, #home_bit, .footer-distributed .footer-center p a, .fas, .far, .fab {
    color: #e11a80;
}

/* Thread/Post Count */
.thread-count, .view-count {
    color: #e11a80;
}

/* Footer Border */
.footer-leftright {
    border-left: 3px solid #e11a80;
    border-right: 3px solid #e11a80;
    border-bottom: 3px solid #e11a80;
}
.footer-distributed {
    border-top: 3px solid #e11a80;
}
.footer-h3 {
    color: #e11a80;
}
}
