css:
@-moz-document domain("vortaro.net") {
.article,
.clearfix,
.derivo,
h3,
i,
label,
.main,
p,
.signifonuanco i,
span i,
th,
td {
    color: azure;
}

.article i,
.derivo i,
.signifonuanco {
    color: lightgray
}

.article span.fako {
    color: chocolate
}

.article .signifonuanco b {
    color: pink
}

.article strong {
    color: gray
}

.article sup {
    text-shadow: 1px 1px #000;
}

body,
html {
    background-color: #222;
}

.buttons,
buttons-content,
#tools-wrap,
.fixed {
    background-color: #222;
    background: none;
}

dfn {
    color: skyblue;
}

.fako {
    color: blue;
}

.highlight {
    background-color: tomato;
    color: black;
}

.socialicons {
    display: none
}

.title {
    color: darkgreen;
}

.Vortoreferenco,
.vicon {
    font-size: 1.3rem;
    font-weight: bold;
}

span .fako .granda .vicon .title {
    color: white;
}

table.dat tr td {
    background-color: #111
}

table.dat tr:nth-child(odd) td {
    background-color: #333
}


th,
tr {
    color: #222;
    background-color: #222
}

#tools a {
    color: skyblue
}
}
