css:
@-moz-document domain("justdancenow.com") {
/*---------------Menu----------------*/

.danceroom__qr-code-wrapper, .vip:not(.state-dance) .account, .vip:not(.state-tutorial) .account, .account, .vip:not(.state-dance) #room-info::after, .vip:not(.state-tutorial) #room-info::after, #room-info::before
{
  display: none;
}

#just-dance-now {
	background: url(https://www.dropbox.com/s/a7c6d1wm4pzpvt4/MenuRemake2.png?raw=1) center/cover no-repeat;
	background-size: 100% 100%!important;
	background-size: cover;
	background-repeat: no-repeat;
	background-position: center
}

#just-dance-now .tutorial {
	background: url(https://www.dropbox.com/s/a7c6d1wm4pzpvt4/MenuRemake2.png?raw=1) center/cover no-repeat;
	background-size: 100% 100%!important;
	background-size: cover;
	background-repeat: no-repeat;
	background-position: center
}

#toast
{
    background: url(https://www.dropbox.com/s/ut89w7ogc6x2gpn/NotificationRemake%20%281%29.png?raw=1);
        	background-size: 100% 100%!important;
	background-size: cover;
	background-repeat: no-repeat;
	background-position: center
}

@font-face
{
    font-family: "Just_Dance_Bold";
    src: url('https://dl.dropboxusercontent.com/s/at5kzj0kmehb6nr/Just_Dance_Bold.woff?dl=0') format('woff')
}

.danceroom__number,
    .danceroom__label
{
    font-family: Just_Dance_Bold !important;
    text-shadow: none !important;
    color: white !important
}

.sprite-description
{
    font-family: Just_Dance_Bold;
}
.caption.artist
{
    display: none !important
}

.state-coachselection #song-selection:after,
.state-demonotice #song-selection:after
{
  box-shadow: inset 0 7.5em 0.35em #00000094;
  z-index: 1;
  visibility: visible;
  opacity: 0.95;
  display: block;
}

.caption.title
{
    top: -50% !important;
    text-align: center;
    opacity: 0.6;
    text-shadow: 0px 0px 10px black !important;
}

#song-selection:before {
    box-shadow: none;
}

.song.song--focused .caption.title
{
    opacity: 1 !important
}

#song-selection
{
    pointer-events: none !important
}

.category.list.list--x::before
{
    font-family: Just_Dance_Bold;
    opacity: 1 !important
}

#toast .toast__content
{
    color: white
}

#toast .toast__color
{
    display: none;
}

.song__cover
{
    border-style: solid;
    border-width: 1.5px;
    border-radius: 5px;
    border-color: grey;
    box-shadow: 0px 0px 10px grey;
}

.toggle::before{border-left:none}
.toggle{fill:rgb(143, 135, 145)!important;right:3%}
.toggle::after{background-color:rgba(255, 0, 253, 0)}
#settings{background-image:url(https://www.dropbox.com/s/p6y5h6eldaxooax/Blank.png?raw=1);
background-size:100%;background-repeat:no-repeat;background-position:0% 80%;right:0.65%;}
@font-face {
    font-family: "Bahnschrift SemiBold";
    src: url('Bahnschrift SemiBold.woff2') format('woff2'),
        url('Bahnschrift SemiBold.woff') format('woff');
}

.sprite-description
{
    top: 110% !important;
    left: 30% !important;
}

#preview .song-details h3
{
    padding-left:0%;
    font-family: Just_Dance_Bold
}

.main
{
    background-image: url(https://www.dropbox.com/s/8u456khnsp3o6xc/JustDanceOldie.PNG?raw=1) !important;
        background-color: rgba(0, 0, 0, 0) !important;
    background-size: cover;
    animation: BrightnessCoverBeat 2s linear infinite;
}

.dr-landing.launch-game.visible, .init-spinner, .dr-landing.launch-game.visible::before
{
    display: none;
}

.song-detail__artist
{
    left: -10% !important;
}

.landing-button.launch-game.visible
{
    opacity: 0;
    top: 54%;
    position: fixed;
    transform: scale(0.9)
}

video.landing__video,
.footer,
.footer .copyright,
nav,
.footer__ubi-logo,
.footer .menu,
h1.landing-text__title,
.logo__inner,
h2.landing-text__subtitle,
.side-nav,
button.landing-arrow.scroll_down,
.landing-button::before,
.section.availability, .logo,
.section--vertical-content,
.section#how-it-works,
section#explore, .main::before{
    display: none !important;
}


#room-info::before
{
  left: 0.5em;
  top: -211%;
  width: 0%;
}

.danceroom__number
{
    border: none !important
}

.danceroom__label
{
    color: #fff;
}

.video-preview
{
    display: none;
}

.preview--bg
{
    display: none;
}

.state-coachselection #preview .song-details--empty {
    display: none;
}

.coach-selection__item.coach::before
{
    background-image: url(https://www.dropbox.com/s/wh9hild1okt8h01/CoachHighlight.png?raw=1);
    left: 10%;
}

.coach__histogram.coach__histogram--visible::before
{
    display: none;
}

.coach__names-list
{
    top: 50%;
    left: 15%
}

#afterdance.transition-in::before
{
    left: 550%;
}

.img--afterDance::before
{
    display: none;
}

.song.song--focused .song__cover {
        border-style: solid;
        border-width: 1.5px;
        border-radius: 5px;
        border-color: #fff;
    box-shadow: 0px 0px 10px #fff;
    }

.song-cover
{
    box-shadow: 0px 0px 10px grey;
}

.state-coachselection .song-cover
{
    animation: none !important;
    transform: scale(1.3);
    top: 10% !important
}

.state-coachselection #song-selection
{
    top: 10% !important;
}

.coach-selection__title
{
    left: 10%
}

.state-coachselection .song-action__button
{
    animation: none !important;
    top: 30% !important
}

.song-action__label
{
    display: none !important;
}

.song-action__button
{
    background-image: url(https://www.dropbox.com/s/lnvqvsejifgcj69/StartButton.png?raw=1) !important;
    top: 20%
}

.state-coachselection .song-action__button
{
    background-image: url(https://www.dropbox.com/s/lnvqvsejifgcj69/StartButton.png?raw=1) !important;
    top: -10%
}

.dancercard .player-flag__image
{
	height:63%;
	width:63%;
	-webkit-mask-image: url(https://vignette.wikia.nocookie.net/agk/images/1/16/Black_Circle.png/revision/latest?cb=20151220003202);
    -webkit-mask-size: 90%;
    -webkit-mask-position: center;
    -webkit-mask-repeat: no-repeat;
}

.song-action__price-tag
{
    display: none !important;
}

/*-----------------SongCovers&Info---------------*/

.song-cover--hi-res
{
    border-radius: 10px;
    left: 50%
}

.song__cover
{
    border-radius: 10px !important;
}

.song--focused .song__decoration::after {
display: none;
}

.song--focused
{
    top: -7%;
    transform: scale(1.1)
}

.sprite-description, .sprites-container, .sprite__svg-container
{
   transform: scale(0.8);
    top: 20%;
    left: 10%;
    animation: spinner01 10s linear infinite !important;
}

.controller .dancercard .player-master
{
    display: none;
}

.dancercard .player-flag
{
    left: 70%
}

#players .controller-coins {
display: none;
}

.feedback-label
{
    top: 80% !important;
}

#crown-container.purple, #crown-container.red, #crown-container.blue, #crown-container.green
{
    top: 27% !important
}

.exit-btn.exit-btn--play-again.exit-btn--animate
{
    color: white
}

.exit-btn.exit-btn--play-again.exit-btn--animate::before
{
    color: white !important
}

  #song-selection
  {
    background-image: url(https://www.dropbox.com/s/xq0fvm2zq994c16/MenuRemake3.png?raw=1);
    background-size: cover;
  }

.coach-selection__list.coach-selection__list--4
{
    left: 10%;
    top: 30%
}

.coach-selection__list.coach-selection__list--3
{
    left: 10%;
    top: 30%
}

.coach-selection__list.coach-selection__list--2
{
    left: 10%;
    top: 30%
}

.coach-selection__list.coach-selection__list--1
{
    left: 10%;
    top: 30%
}

#afterdance .players
{
    top: 20%;
}

#afterdance .song-info
{
    top: 5%;
    left: 30%;
}

.avatar-wrapper
{
    transform: scale(0.8);
    animation: none !important
}

.song.song--focused .title-container
{
    display: block !important;
    top: 80%;
    color: white !important
}

.menu__body, .menu__head, .legal__list
{
    background-image: url(https://www.dropbox.com/s/xq0fvm2zq994c16/MenuRemake3.png?raw=1)
}


.modal__container::before
{
    background-image: url(https://www.dropbox.com/s/xq0fvm2zq994c16/MenuRemake3.png?raw=1);
    background-size: cover;
    left: -30%;
    width: 110%;
    height: 150%
}

.modal__container.modal__container--active::after {
        display: none;
    }

#afterdance .song-info::before
{
display: none;
}

#crown-container.blue
{
  background: url(https://dl.dropbox.com/s/37nbzv3xn7fg22v/Blue%20-%20Crown.png?dl=0);
    width: 11%;
}

#crown-container.aqua
{
  background: url(https://www.dropbox.com/s/iuezj5w685o9ib5/p-replace-color%20%283%29.png?raw=1);
    width: 11%;
}

#crown-container.red
{
  background: url(https://dl.dropbox.com/s/37nbzv3xn7fg22v/Blue%20-%20Crown.png?dl=0);
    width: 11%;
}

#crown-container.green
{
  background: url(https://dl.dropbox.com/s/1s4ynkior1aw774/Green%20-%20Crown.png?dl=0);
    width: 11%
}

#crown-container.purple
{
  background: url(https://dl.dropbox.com/s/o0i8kl6ky4glc3s/Purple%20-%20Crown.png?dl=0);
    width: 11%
}

#crown-container.red
{
  background: url(https://dl.dropbox.com/s/svktgl5uej7qssu/Red%20-%20Crown.png?dl=0),url(https://dl.dropbox.com/s/gyvh1opt7tmozxh/WinnerRED.png?dl=0);
    width: 11%;
}

#afterdance .crown
{
    display: none;
}

.highScore.highScore__show.show
{
    display: none !important
}

.dancercard .player-color, .player--new .player-color {
    transform: scale(0.6, 0.6);
    top: -5%;
    left: -15%;
    animation: none !important;
        border-radius: 26px;
    	-webkit-mask-image: url(https://i.imgur.com/lSMMj7D.png);
    -webkit-mask-size: 40%;
    -webkit-mask-position: center;
    -webkit-mask-repeat: no-repeat;
}

.song-cover--low-res
{
    display: none;
}

.pre-beat.odd .star.star-1
{
  animation: ScoreDisplay 0.9s both infinite !important;
}

.pre-beat.even .star.star-2
{
  animation: ScoreDisplay 0.9s both infinite !important;
}

.pre-beat.odd .star.star-3
{
  animation: ScoreDisplay 0.9s both infinite !important;
}

.pre-beat.even .star.star-4
{
  animation: ScoreDisplay 0.9s both infinite !important;
}

.pre-beat.odd .star.star-5
{
  animation: ScoreDisplay 0.9s both infinite !important;
}

.star.star-1, .star.star-2, .star.star-3, .star.star-4, .star.star-5
{
    transform: rotate(0deg) !important
}

#202121 .coach__player-list:nth-child(1) {
        margin-right: -.035rem;
    }
    .coach__player-list:nth-child(2) {
        margin-left: -.035rem;
    }
    .coach__player-list:nth-child(3) {
        margin-left: -.035rem;
    }
    .coach__player-list:nth-child(4) {
        margin-left: -.035rem;
    }

.coach__player-list::before {
        transform: rotate(0deg);
        border-radius: 5px;
        opacity: 1;
    }

.danceroom__qr-code-wrapper
{
    display: none;
}

.player-exp
{
    display: none !important;
}

.dancercard .player-name em
{
    font-size: 34px;
}

.song-detail__artist
{
    left: -70% !important
}

#preview .song-details
{
top: -70%;
    left: 10%
}


.song-detail.song__infos::before, .song-detail.song__reward, .score__number::before, .score__display::after
{
    display: none;
}

.score__number
{
    top: -50%;
    left: 100%;
    transform: scale(1.9);
    color: Yellow;
    transition: none !important
}

.score__owner
{
    transform: scale(1.5);
    color: white;
        top: -110%;
    left: 110%;
    transition: none !important
}

.highscore-display.highscore-display--small
{
    display: none !important
}

.score__display, .highscore-display, .highscore-display.highscore-display--small
{
    transition: none !important
}

.song-cover--low-res
{
    left: 150% !important
}

.song-cover--low-res
{
  animation: PS 1s steps(6,end) infinite,BrightnessBeat 2s both infinite;
  background-size: 100%;
  background-repeat: no-repeat;
  top: 0;
  left: 0;
  right: 0;
  width: 100%;
  bottom: 0;
}

.owner-name
{
    text-align: center !important;
    width: 50% !important;
}

.score__number
{
    text-align: center !important;
    width: 130% !important;
    left: 95%
}

.pre-beat .song-cover--hi-res, .pre-beat.even .item-container.item-container--x .song.song--focused
{
    animation: Cover 1s ease-in-out infinite
}

.pre-beat.odd .song-cover--hi-res, .pre-beat.odd .item-container.item-container--x .song.song--focused
{
    animation: Cover2 1s ease-in-out infinite
}

.pre-beat.even .song-action__button
{
    animation: Cover 1s ease-in-out infinite
}

.pre-beat.odd .song-action__button
{
    animation: Cover2 1s ease-in-out infinite
}

html:not(.ipad) .hud.beat>#players.animate .player-color {
    animation: DanceAnimation 1s infinite !important;
}

/*--------------Animations--------------*/

@keyframes Cover
{
    0%
    {
        transform: rotate(2deg);
            transform: scale(1.05)
    }
    
    100%
    {
        transform: rotate(-2deg);
    }
}

@keyframes Cover2
{
    0%
    {
        transform: rotate(-2deg);
            transform: scale(1.05)
    }
    
    100%
    {
        transform: rotate(2deg);
    }
    
}

@keyframes Cover3
{
    0%
    {
        transform: rotate(-2deg);
            transform: scale(1.05)
    }
    
    50%
    {
        transform: rotate(0deg);
    }
    
    100%
    {
        transform: rotate(2deg);
        transform: scale(1.05)
    }
    
}


@keyframes ScoreDisplay
{
  0%
  {
    transform: scale(0.9);
  }

  50%
  {
    transform: scale(1.1);
  }
    
 100%
  {
    transform: scale(0.9);
  }
}

@keyframes DanceAnimation
{
    0%
    {
        transform: scale(0.6, 0.6)
    }
    
    100%
    {
        transform: scale(0.5, 0.5)
    }
}
}
