css:
@-moz-document url-prefix("https://colab.research.google.com/") {
body {
  --code-cell-background: #222;
  --colab-primary-surface-color:#112;
  --colab-primary-text-color:#7bf;
  --colab-primary-background-color:#111;
  --colab-secondary-surface-color:#666;
  --colab-toolbar-surface-color: #444;
  --primary-background-color: #222;
  --colab-highlighted-surface-color: #99a;
  --colab-tertiary-text-color:#444;
  --colab-border-color:#777;
  --colab-page-background-surface-color: #555;    
  --paper-input-container-input-color:#ccc;
  --paper-checkbox-label-color:#ccc;
}

p, h1, h2, h3, h4, h5, h6, .cell.text, .markdown, .main-content, .notebook-content, .add-cell, .cell, .text, .formview, .formview-title {
    font-size:14px;
}

paper-item {
    cursor: default;
    color: whitesmoke;
}

#top-toolbar {
    background-color:#aab;
}

.cm-s-default .cm-def {
    color: #ffd600;
    font-size: 16px;
}

.notebook-content-background {
    background-color:var(--colab-primary-background-color);
}

colab-file-tree {
    display: flex;
    flex-flow: column;
    flex-grow: 1;
    overflow-y: auto;
    color: var(--colab-primary-text-color);
}
.layout.vertical {
    display: flex;
    flex-direction: column;
    background: #222;
}
.tooltiptext {
    background-color:#222;
    border: 0;
    border-radius: 0;
    max-height: 500px;
    overflow: auto;
    white-space: pre-wrap;
    font-size:14px;
    color:azure;
    padding:10px;
}
.text.edit .markdown-toolbar {
    background-color:#bbb;
}

colab-dialog-impl {
    background:#fff !important;
}

.code .cm-matchhighlight.CodeMirror-selectedtext {
    background: #4285f4;
}

.code .cm-matchhighlight {
    background: #21a202;
}

.goog-menubar .goog-menu-button {
    border: 1px solid #444;
}

 

span.CodeMirror-selectedtext {
    background: #44f;
}

.goog-menu, .goog-menuitem, .goog-menuseparator.goog-menuitem-disabled, 
.goog-menuitem-content, .editor.flex {
    color: #ddd;
    background: #444;
    border-color: #666; 
}

.goog-menuitem-accel {
    color: #888;
    background: #444;
}

.paper-material-0[elevation="2"] {
    box-shadow: 1px 1px 5px 3px #888b /* , 0 3px 3px 0  #8888, 0 3px 3px -1px  #8888; */
}

.add-cell {
    background: #8880! important;    
}

.CodeMirror-code {
    color: #ccc!important;
    background: #222;
}

.CodeMirror pre, 
.CodeMirror.cm-s-default.CodeMirror-simplescroll {
    color: #ccc;
    background: #222;
}

.CodeMirror-activeline pre {
    background: #333;
    line-height: 21px;    
}

pre.CodeMirror-line {
    color: #73a96a;   
    line-height: 21px;
}

.CodeMirror-matchingbracket {
    color: #8ff!important;
}

.CodeMirror-cursor {
    border-left: 1px solid #0f0;
}

/* gutters */

.code.output-selected .output-info  {
    background: #555;
}

.cell-gutter{
    background: #222;
}

.code.focused.output-selected .cell-gutter {
    color: #ccc;
    background: #222;
}

.code.focused .cell-gutter {
    color: #ccc;
    background: #555;
}

.output-info {
    color: #ccc;
    background: #000;
}

.code.focused .cell-execution-indicator iron-icon {
    color: #ccc;
}

/* markdown */

.text.edit .cm-header {
    color: #8ff;
}

.text.edit .markdown,
.text.edit .CodeMirror,
.text.edit .cm-variable-2,
.text.edit .cm-comment,
.text.edit .cm-quote {
    color: #f88;
    font-family: 'Roboto', 'Noto', sans-serif;
    font-size: 14px
}
.text.edit .markdown a,
.text.edit .cm-link,
.text.edit .cm-url,
.text.edit .cm-header.cm-link,
.text.edit .cm-header.cm-url {
    color: #88f
}

.text.edit .CodeMirror .markdown-mathjax {
    color: #f80;
    font-family: Georgia, "Times New Roman", Times, serif;
    font-style: italic;
    font-weight: 400;
    letter-spacing: 1px
}

.cm-s-default .cm-keyword {
    color: #0ff;
    font-weight: bold;
    font-size:16px;
}
.cm-s-default .cm-atom {
    color: #f0f
}
.cm-s-default .cm-number {
    color: #fa0;
    font-weight: bold;
    font-size:14px;
}

.cm-s-default .cm-variable,
.cm-s-default .cm-property{
    color: #dde;
    font-size:16px;
    line-height:21px;
    
}

.cm-s-default .cm-punctuation,
.cm-s-default .cm-operator {
    color: #5ff;
    font-weight: bold;
}

.cm-s-default .cm-variable-2 {
    color: #88f
}
.cm-s-default .cm-variable-3,
.cm-s-default .cm-type {
    color: #085
}
.cm-s-default .cm-comment {
    color: #9ca7bd;  
    font-size:14px;
    font-style:italic;
    line-height:18px;
/*    font-weight: bold;    */
}
.cm-s-default .cm-string {
    color: #0f0;
    font-weight: bold;    
    font-size:14px;
}
.cm-s-default .cm-string-2 {
    color: #8f0;
    font-size:14px;
}
.cm-s-default .cm-meta {
    color: #f80;
    font-size:14px;
}
.cm-s-default .cm-qualifier {
    color: #555;
    font-size:14px;
}
.cm-s-default .cm-builtin {
    color: #fff;
    font-size:16px;
}
.cm-s-default .cm-bracket {
    color: #eea
}
.cm-s-default .cm-tag {
    color: #fff;
    font-weight: bold;    
}
.cm-s-default .cm-attribute {
    color: #00c
}
.cm-s-default .cm-hr {
    color: #999
}

a, .cm-s-default .cm-link {
    color: #88f
}
.cm-s-default .cm-error {
    color: #000;
    background: #f00;
}
.cm-invalidchar {
    color: #000;
    background: #f00;
}

.content-area, .error-message, .scrollable, .buttons {
    background-color: #000;
    color: #ccc;
}

.paper-button {
    color: #ccc;
    opacity: 0.3;
    background-color: #888;
    border: 2px;
    border-color: #fff;
}
.iron-overlay-backdrop-0 {
    background-color: #888;
}

.iron-overlay-backdrop-0.opened {
    opacity: 0.5;
}

#iron-pages, #iron-overlay-backdrop {
    background: #eef;
    color:#222;
}
}
