css:
@-moz-document domain("sbup.com") {
body {
    background-color: #eee;
}

#wrapper {
    height: 100%;
    min-height: 0;
    max-width: 1024px;
    margin: 0 auto;
    padding: 1rem;
    background-color: transparent;
}

#header,
#vk_like,
#pg_lnks,
#dc-slick-23,
#yandex_ad_tls_btm,
#footer,
#sidebar object,
#b,
#widget,
#twitter-widget-0,
#informers,
#social_books {
    display: none;
}
#headT {
    background: transparent;
    border: 0;
    height: auto;
    line-height: 1;
    margin: 0;
    text-shadow: none;
}
#headT h1 {
    color: #000;
    padding: 1rem 0;
}
.blueheader,
.yellowheader {
    border: 0;
    border-radius: 0 !important;
    background: #fff !important;
    min-height: 0;
    line-height: 1.2;
    font-size: 1rem !important;
}
.blueheader h2,
.yellowheader h2 {
    color: #204BCE;
    text-shadow: none;
    font-size: 1.5rem;
    padding: 1.5rem 0;
}
.yellowheader .h2,
.yellowheader h2 {
    color: #222;
}
.yellowblock {
    margin: 5px 7px 5px 7px;
    border: 0;
    padding: 1rem;
    background-color: #fff;
}
.newsidevar {
    background: #fff;
    border-radius: 0 !important;
    box-shadow: none !important;
    -webkit-box-shadow: none !important;
    width: 289px;
    padding: 7px 3px 7px 5px;
    margin-bottom: 12px;
    border: 0;
}
.newsidevar ul li {
    list-style-image: none;
}
.menuheaders {
    background: #fff;
    border: 0;
    text-shadow: none;
    color: #000;
}


#sbmenu li {
    background: transparent;
}

#inf_header,
.br_hd {
    background-color: transparent;
    color: #204BCE;
    font-family: Verdana, Arial, Helvetica, sans-serif;
    font-size: 1.5rem;
    padding: 1.5rem 0;
}

#geo_tr {
    overflow-x: auto;
}

.buttonurlsubmit {
    border-radius: 2px;
    text-transform: uppercase;
    border: 0;
    text-shadow: none;
    color: #fff;
    background: #204BCE;
    height: auto;
    padding: 1rem 2rem;
    font-size: 1rem;
    width: auto;
}
.buttonurlsubmit:hover {
    border-radius: 2px;
    text-transform: uppercase;
    border: 0;
    text-shadow: none;
    color: #fff;
    background: #2a58e2;
    height: auto;
    padding: 1rem 2rem;
    font-size: 1rem;
    width: auto;
}
.buturlsub_no_over {
    border-radius: 2px;
    text-transform: uppercase;
    border: 0;
    text-shadow: none;
    color: #fff;
    background: #5b7fec;
    height: auto;
    padding: 1rem 1rem;
    font-size: 1rem;
    width: auto;
}
.formurliput {
    border-radius: 2px;
    width: 100%;
    height: auto;
    font-family: Verdana, Arial, Helvetica, sans-serif;
    font-size: 1rem;
    text-indent: 3px;
    border: 1px solid #666;
    border-style: inset;
    margin: 0 1rem 0 0;
}

.forminputmain form {
    display: flex;
}

.line_margin {
    font-family: Verdana, Arial, Helvetica, sans-serif;
    line-height: 1.2;
}
}
