css:
@-moz-document domain("presearch.org") {
body {
    background: linear-gradient(0deg, #1d1d1d, #000000);
}

.input-group-addon,
.input-group-btn,
.input-group .form-control {
    background: transparent;
}

.input-group-btn,
.input-group-btn > .btn {
    background: transparent;
}

.btn-default {
    color: #ccd0d2;
}

.form-control:focus {
    border-color: #4c4c4c;
    outline: 0;
    -webkit-box-shadow: inset 0 1px 1px rgba(0, 0, 0, .075), 0 0 8px rgba(152, 203, 232, .6);
    box-shadow: inset 0 1px 1px rgba(0, 0, 0, .075), 0 0 8px rgba(255, 255, 255, 0.6);
}

.btn-default.active,
.btn-default:active,
.btn-default:hover,
.open > .btn-default.dropdown-toggle {
    color: #4c4c4c;
    border-color: #4c4c4c;
    -webkit-box-shadow: inset 0 1px 1px rgba(0, 0, 0, .075), 0 0 8px rgba(152, 203, 232, .6);
    box-shadow: inset 0 1px 1px rgba(0, 0, 0, .075), 0 0 8px rgba(255, 255, 255, 0.6);
}

/* Search site */
.search-results .results-row:nth-of-type(2n) > div,
.user-collections .results-row:nth-of-type(2n) > div {
    background: #272727;
}

.navbar-default {
    background-color: #191919;
}

.form-control,
output {
    color: #fff;
}

/* Account Site */
body.account-nosidebar, body.has-sidebar {
    background: #232323;
    color: #fff;
}

.sidebar {
    background: #1b1b1b;
}

.sidebar .sidebar-nav li a {
    color: #fff;
}

.pod .pod-header {
    background: #232323;
}

.pod .pod-body {
    background: #2f2f2f;
}

.form-control {
    background-color: #2f2f2f;
    border: #ffffff;
}

table.dataTable tbody th, table.dataTable tbody td {
    background: #353535;
}

.table-bordered, .table-bordered>tbody>tr>td, .table-bordered>tbody>tr>th, .table-bordered>tfoot>tr>td, .table-bordered>tfoot>tr>th, .table-bordered>thead>tr>td, .table-bordered>thead>tr>th {
    border: 1px solid #232323;
}

.dataTables_wrapper .dataTables_length, .dataTables_wrapper .dataTables_filter, .dataTables_wrapper .dataTables_info, .dataTables_wrapper .dataTables_processing, .dataTables_wrapper .dataTables_paginate {
    color: #fff;
}

.table-striped>tbody>tr:nth-of-type(odd) {
    background-color: #282828;
}
}
