css:
@-moz-document domain("www.liberafolio.org") {
body {
    background-color: #222;
    color: azure;
}

.site-branding,
#masthead {
    display: none;
}

.main-navigation {
    padding-top: 30px;
    background-color: #333
}

.menu-menu-1-container li,
.menu-item,
h1 .entry-title {
    color: azure;
}

.main-navigation a,
h2.entry-title a,
h1.entry-title,
.widget-title,
.footer-staff-picks h3 {
    color: azure;
}

.rsswidget {
    color: azure;
}

.comment-excerpt,
.cwp-comment-excerpt {
    color: azure;
}

.widget a {
    color: darkgreen;
}

.wc-comment-text,
.wc-comment-footer,
#jp-relatedposts h3.jp-relatedposts-headline em {
    background-color: #222;
    color: azure;
}

.wc-comment-author,
.wc-comment-header {
    background-color: #222;
}

.wc-comment-text p {
    font-size: 1.1rem !important;
}

.entry-content P {
    font-size: 1.1rem;
}

.jp-relatedposts-post-a {
    color: darkgreen;
}

.single-post .navigation .nav-links h5 a {
    color: darkgreen;
}

.sd-sharing {
    display: none;
}
}
