css:
@-moz-document domain("vortaro.esperanto.org.br") {
a {
    color: darkgreen !important;
    font-size: 1.2rem;
    text-decoration: none;
}

a:visited {
    color: chocolate;
}

body,
html {
    background: none;
    background-color: #222;
}

b,
i,
span,
font,
strong {
    color: azure !important;
    font-family: Constantia, "Times New Roman", serif !important;
    font-size: 1.2rem !important;
}

p > b > span {
    color: #009080 !important;
}

td > p {
    color: #bbb;
    padding: 3
}

.section1 {
    display: flex;
    flex-direction: column;
    max-width: 85vw;
    justify-content: space-between;
    padding: 5vw;
}

frame,
iframe {
    width: 100%
}

.style15 {
    font-family: Constantia, "Times New Roman", serif;
}
}

@-moz-document url-prefix("http://vortaro.esperanto.org.br/butonaro.htm") {
html {
    padding: 0;
    margin: 0;
    /*position: relative;
    left: -50px;*/
    overflow: hidden;
    display: flex;
    justify-content: center;
    align-items: center;
    align-self: center;
    word-wrap: break-word;
    font-family: Constantia;
}

td {
    padding: 10px;
}

.style10 {
    font-size: 1.1rem;
}

.style14,
.style16 {
    font-family: Constantia;
}

.style16 {
    word-wrap: break-word;
}


.style16 strong {
    font-size: .5rem;
}
.style10 {
    /* display: none*/
}
}

@-moz-document url-prefix("http://vortaro.esperanto.org.br/fulmsercxilo.htm") {
* {
    color: azure !important;
    font-family: Constantia, "Times New Roman", serif;
    font-size: 1.2rem;
}

input {
    color: black !important;
}

img {
    background-image: url(http://pngimg.com/uploads/google/google_PNG19638.png);
}
}

@-moz-document url-prefix("http://vortaro.esperanto.org.br/ee_index.html") {
html,
body,
span,
.Section1,
p {
    font-family: Constantia, "Times New Roman", serif !important;
    color: azure;
}
}

@-moz-document url-prefix("http://vortaro.esperanto.org.br/ee_butonaro.htm") {
html {
    padding: 0;
    margin: 0;
    /*position: relative;
    left: -50px;*/
    overflow: hidden;
    display: flex;
    justify-content: center;
    align-items: center;
    align-self: center;
    word-wrap: break-word;
    font-family: Constantia;
}

td {
    padding: 10px;
}

.style17 {
    color: azure !important;
}
}
