/* ==UserStyle==
@name        Ember Twiddle Solarized
@namespace   Sukima
@version     1.0.0
@homepageURL https://github.com/sukima/ember-twiddle-solarized
@updateURL   https://raw.githubusercontent.com/sukima/ember-twiddle-solarized/master/ember-twiddle-solarized.user.css
@license     CC-BY-SA-4.0
@author      Sukima <suki@tritarget.org>
@advanced dropdown solarized-theme "Solarized theme" {
  Dark "Solarized Dark" <<<EOT
  :root {
    --color-base03: #002b36;
    --color-base02: #073642;
    --color-base01: #586e75;
    --color-base00: #657b83;
    --color-base0: #839496;
    --color-base1: #93a1a1;
    --color-base2: #eee8d5;
    --color-base3: #fdf6e3;
    --color-yellow: #b58900;
    --color-orange: #cb4b16;
    --color-red: #dc322f;
    --color-magenta: #d33682;
    --color-violet: #6c71c4;
    --color-blue: #268bd2;
    --color-cyan: #2aa198;
    --color-green: #859900;
    --color-dropdown-bg: var(--color-base2);
  } EOT;
  Light "Solarized Light" <<<EOT
  :root {
    --color-base3: #002b36;
    --color-base2: #073642;
    --color-base1: #586e75;
    --color-base0: #657b83;
    --color-base00: #839496;
    --color-base01: #93a1a1;
    --color-base02: #eee8d5;
    --color-base03: #fdf6e3;
    --color-yellow: #b58900;
    --color-orange: #cb4b16;
    --color-red: #dc322f;
    --color-magenta: #d33682;
    --color-violet: #6c71c4;
    --color-blue: #268bd2;
    --color-cyan: #2aa198;
    --color-green: #859900;
    --color-dropdown-bg: var(--color-base02);
  } EOT;
}
==/UserStyle== */

@-moz-document domain("ember-twiddle.com") {

  html * {
    color-profile: sRGB;
    rendering-intent: auto;
  }

  /*[[solarized-theme]]*/

  .navbar-default,
  .navbar-default .toolbar {
    color: var(--color-base03);
    background-color: var(--color-orange);
  }
  input[type="checkbox"],
  input[type="radio"] {
    background-color: var(--color-base03);
    color: var(--color-base0);
  }
  .navbar-default .dropdown-menu,
  .twiddle-pane .header .dropdown-menu,
  .twiddle-pane.file-tree {
    background-color: var(--color-base02);
  }
  .cm-s-default {
    background-color: var(--color-base03);
  }
  .navbar-default .dropdown-menu,
  .navbar-default .dropdown-menu li,
  .twiddle-pane .header .dropdown-menu,
  .twiddle-pane .header .dropdown-menu li {
    background-color: var(--color-dropdown-bg);
  }
  .navbar-default .dropdown-menu,
  .navbar-default .dropdown-menu li,
  .twiddle-pane .header .dropdown-menu li,
  .twiddle-pane .header .dropdown-menu,
  .twiddle-pane.file-tree,
  .cm-s-default {
    color: var(--color-base0);
  }
  .navbar-default .dropdown-menu li.divider {
    background-color: var(--color-base0);
  }
  .twiddle-pane {
    border-right: 1px solid var(--color-base0) !important;
  }
  .twiddle-pane .header {
    border-bottom: 1px solid var(--color-base03) !important;
  }
  .nav > li > a,
  .nav > li > a:focus,
  .nav > li > a:hover,
  .twiddle-pane .header {
    background-color: transparent;
  }
  .twiddle-pane .header {
    color: var(--color-base0);
  }
  .twiddle-pane .header {
    background-color: var(--color-base03);
  }
  .twiddle-panes .header .twiddlicon:hover,
  .twiddle-panes .header .twiddlicon,
  .twiddle-panes .header .glyphicon:hover,
  .twiddle-panes .header .glyphicon {
    color: var(--color-base0);
    text-shadow: none;
  }
  .light-gray {
    color: var(--color-base1);
  }
  .jstree-default .jstree-hovered .light-gray,
  .jstree-default .jstree-clicked .light-gray {
    color: var(--color-base03);
  }
  .nav .open > a,
  .nav .open > a:focus,
  .nav .open > a:hover,
  .jstree-default .jstree-clicked {
    background-color: var(--color-violet);
  }
  .nav > li > a {
    color: var(--color-base0);
  }
  .nav > li > a:focus,
  .nav > li > a:hover {
    color: var(--color-base03);
  }
  .navbar-default .nav > li > a,
  .navbar-default .nav > li > a:focus,
  .navbar-default .nav > li > a:hover {
    color: var(--color-base3);
  }
  .jstree-default .jstree-clicked {
    color: var(--color-base03);
  }
  .nav > li > a:hover,
  .dropdown-menu > li > a:focus,
  .dropdown-menu > li > a:hover,
  .jstree-default .jstree-hovered {
    background-color: var(--color-cyan);
    color: var(--color-base03);
  }
  .CodeMirror-gutters {
    border-right: 1px solid var(--color-base03) !important;
  }
  .cm-s-default .emphasis {
    font-weight: bold;
  }
  .cm-s-default .dotted {
    border-bottom: 1px dotted var(--color-orange);
  }
  .cm-s-default .CodeMirror-gutter {
    background-color: var(--color-base02);
    border-right: 3px solid var(--color-base02);
  }
  .cm-s-default .CodeMirror-gutter .CodeMirror-gutter-text {
    color: var(--color-base01);
  }
  .cm-s-default .CodeMirror-cursor {
    border-left-color: var(--color-base3) !important;
  }
  .cm-s-default .CodeMirror-matchingbracket {
    color: var(--color-base3);
    background-color: var(--color-base02);
    box-shadow: 0 0 10px var(--color-base02);
    font-weight: bold;
  }
  .cm-s-default .CodeMirror-nonmatchingbracket {
    background-color: var(--color-base02);
    box-shadow: 0 0 10px var(--color-base02);
    font-weight: bold;
    color: var(--color-red);
    border-bottom: 1px dotted var(--color-orange);
  }
  .cm-s-default span.cm-keyword {
    color: var(--color-base0);
    font-weight: bold;
  }
  .cm-s-default span.cm-atom {
    color: var(--color-cyan);
  }
  .cm-s-default span.cm-number {
    color: var(--color-base1);
  }
  .cm-s-default span.cm-def {
    color: var(--color-blue);
  }
  .cm-s-default span.cm-variable {
    color: var(--color-orange);
  }
  .cm-s-default span.cm-variable-2 {
    color: var(--color-orange);
  }
  .cm-s-default span.cm-variable-3 {
    color: var(--color-orange);
  }
  .cm-s-default span.cm-comment {
    color: var(--color-base01);
  }
  .cm-s-default span.cm-property {
    color: var(--color-yellow);
  }
  .cm-s-default span.cm-operator {
    color: var(--color-base0);
  }
  .cm-s-default span.cm-string {
    color: var(--color-violet);
  }
  .cm-s-default span.cm-error {
    font-weight: bold;
    border-bottom: 1px dotted var(--color-orange);
  }
  .cm-s-default span.cm-bracket {
    color: var(--color-orange);
  }
  .cm-s-default span.cm-tag {
    color: var(--color-base0);
  }
  .cm-s-default span.cm-attribute {
    color: var(--color-base1);
    font-weight: bold;
  }
  .cm-s-default span.cm-meta {
    color: var(--color-blue);
  }
}
