css:
@-moz-document domain(foodsharing.de) {
  /* Vollton --------------------------- */
.bootstrap .bg-primary, 
.navbar,
.chatboxhead,
footer,
footer .linklist li a,
.ui-widget-header,
.dropdown-item:active,
div.statdisplay .item,
.ui-tooltip,
ul.team li a:hover,
ul.linklist li > a:hover,
ul.linklist li > a:hover span,
.bootstrap .btn-outline-primary:hover{
  background-color: #6cbc5d !important;
}

/* Fix Header manchmal nicht grün */
.bootstrap .bg-primary {background: #6cbc5d !important;}

h1,
h2,
h3,
table.pintable tr.milestone td a{
	color: #6cbc5d  !important;
}

/* Halbton --------------------------- */
div.msg-inside{
 	background-color: #e1ffdb !important; 
}

/* Kontrast --------------------------- */
ul.team li a.verantwortlich{
  background-color: #ede892 !important;
}

/* Schwarz --------------------------- */
p,
ul.linklist li a,
.bootstrap .text-primary,
ul.team li a .infos .item,
.innerRow .label{
	color: #222 !important;
}
/* "ist neu im team" */
table.pintable tr.milestone{
  border: 6px solid #fff !important;
}
table.pintable tr.milestone td{
  padding-top: 0;
  padding-bottom: 0;
}
.bootstrap .btn-outline-primary {
  border-color: #222;
}
  
/* Hellgrau --------------------------- */
body, 
.chatboxmessagecontent,
.dropdown-item:hover,
#section0,
table.pintable tr.milestone,
ul.imglist li a{
  background-color: #e7e7e7 !important;
}

/* Weiß --------------------------- */
#topbar .navbar-brand span,
.bootstrap .btn-outline-primary:hover,
.chatboxhead a{
  color: #fff !important;
}

/* Formen --------------------------- */
.ui-widget-header,
.chatboxhead {
  border-radius: 3px 3px 0 0 !important;
}
.corner-bottom,
.corner-all, .linklist li > a,
.ui-corner-all{
 	border-radius: 3px !important; 
}
#activity ul.linklist li {
 	border-radius: 0 !important; 
  margin-bottom: 4px !important;
}
#activity ul.linklist li:last-child {
  border-radius: 0 0 3px 3px !important;
}

/* Border ---------------------------*/
.bootstrap .btn-outline-primary:hover,
.chatboxhead{
  border: none;
}

/* Typografie --------------------------- */
.regionMenu > a.nav-link{
  font-family: Helvetica, Arial, "lucida grande", tahoma, verdana, arial, sans-serif;
	font-weight: bold;
}

/* Medium an up ============================= */
@media screen and (min-width: 640px) {

  p,
  .chatboxmessagecontent {
    font-size: 14px !important;
    line-height: 18px !important;
	}

}
  
}
