css:
@-moz-document domain("ebzzry.com") {
body {
    background-color: #222;
    color: azure;
}

a {
    color: darkgreen;
    padding: 0 5px 0 5px;
    text-decoration: none;
}

a:visited {
    color: chocolate;
}

code {
    color: azure;
}

h1 {
    border-bottom: 6px solid azure;
}

h2 {
    border-bottom: 2px solid azure;
}

p {
    font-size: 1.2rem;
}

ul li {
    padding: 5px;
}
}
