@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("hattrick.org") {

/* LOGO HATTRICK */
	
	#logo {
	background: transparent url(http://img211.imageshack.us/img211/3142/18193059.png)  no-repeat scroll left top !important;
	height:27px!important;
	top:-5px!important;
        left:380px!important;
	}

/* SFONDO HATTRICK */
 
	body{
	background: url(http://i41.tinypic.com/6od3rk.jpg)   !important;
	}

/* ANNUNCI */

	.insertHeader {	background: url(http://img227.imageshack.us/img227/3851/63929776.png) !important;
	}

/* SCORCIATOIE */

	#shortcuts {	margin-top: -10px !important;
	margin-right:0px !important;
	}

/* TICKER */

	#ticker { position: absolute !important;
	top: 0px !important;
	left: 10px !important;
	}

	#ticker div { 
        background: none !important;
	}

/* TEAMLINKS */

	#teamLinks a{
	text-decoration:none!important;
	font-weight:bold!important;
	font-size: 110%!important;
	color:#b39c52 !important;
	}

	#teamLinks a:hover{
	background: none !important;
	text-decoration: underline !important;
	}

/* H1 H2 H3 */

	h1 {
  	color: #b39c52 !important;
	background: none !important;
	font-size: 20px !important;
	}

	h2 {
  	color: #b39c52 !important;
  	background: none !important;
  	font-size: 13px !important;
	}
	
	h3 {
  	color: #b39c52 !important;
  	background: none !important;
  	font-size: 13px !important;
	border-bottom: 1px solid #b39c52 !important;
	}

	a {
        color: #b39c52 !important;
	text-decoration: none !important;
	}

	a:hover{
	background: none !important;
	text-decoration: underline !important;
	}

/*TITOLI PAGINA SQUADRA*/

	.main .mainBox h2 {
	border: solid 1px #000000!important;
	background: url(http://img227.imageshack.us/img227/3851/63929776.png) !important;
	padding: 3px !important;
	-moz-border-radius: 7px !important;
	-webkit-border-radius: 7px !important;
	}

/*USER-DATA-ORA*/

	div[id="online"] {
        color: #b39c52 !important; 
        float:right!important;
	}

	div[id="time"] {
        color: #b39c52 !important;
        float:right!important;
	}

        .date {
        color: #b39c52 !important;
	}

/* MENU HATTRICK BASE CHIARA */
	
	#menu {
	
	background: url(http://img21.imageshack.us/img21/1723/22264722.png)   no-repeat scroll left top !important;
	-moz-border-radius: 7px !important;
	-webkit-border-radius: 7px !important;
	border: 1px solid #000 !important;
	}

/* MENU HATTRICK BASE SCURA AL PASSAGGIO MOUSE */
	
	#menu a:hover{
	background: none !important;
	text-decoration: underline !important;
	}

/* BOX PARTE ALTA BORDI ARROTONDATI*/
	
	.sidebarBox .boxHead h2, 
	.subMenuBox .boxHead h2, 
	.main .boxHead h2,
	.sidebarBox .boxHead .boxLeft h2,
	.subMenuBox .boxHead .boxLeft h2,
	.main .boxHead .boxLeft h2{
	
	background: url(http://img227.imageshack.us/img227/3851/63929776.png)  no-repeat scroll left top !important;
	-moz-border-radius-topleft : 7px !important;
	-webkit-border-top-left-radius : 7px !important;
	-moz-border-radius-topright : 7px !important;
	-webkit-border-top-right-radius : 7px !important;
	border: 1px solid black !important;
	}

/* BOX PARTE ALTA BORDI ARROTONDATI*/

	.sidebarBox .boxHead , 
	.subMenuBox .boxHead , 
	.main .boxHead ,
	.sidebarBox .boxHead .boxLeft,
	.subMenuBox .boxHead .boxLeft,
	.main .boxHead .boxLeft{
	
	background: transparent none  no-repeat scroll left top !important;
	}

/* BOX PARTE CENTRALE BORDI*/
	
	.sidebarBox .boxBody, 
	.subMenuBox .boxBody, 
	.main .boxBody {
	
	background:#FFFFFF none repeat scroll 0 0!important;
	border-left:1px solid  black !important;
	border-right:1px solid  black !important;
	}

/* BOX PARTE BASSA BORDI ARROTONDATI*/

	.sidebarBox .boxFooter, 
	.subMenuBox .boxFooter, 
	.main .boxFooter {
	
	background: url(http://img57.imageshack.us/img57/8779/54020899.png)  no-repeat scroll left top !important;
	-moz-border-radius-bottomleft : 7px !important;
	-webkit-border-bottom-left-radius : 7px !important;
	-moz-border-radius-bottomright : 7px !important;
	-webkit-border-bottom-right-radius : 7px !important;
	border: 1px solid black !important;
	}

	.sidebarBox .boxFooter .boxLeft, 
	.subMenuBox .boxFooter .boxLeft, 
	.main .boxFooter .boxLeft{
	visibility:hidden !important;
	}	

/* PAGINA BORDI ARROTONDATI*/
	
	#page{
	border: 1px solid black !important;
	-moz-border-radius-topleft : 7px !important;
	-webkit-border-top-left-radius : 7px !important;
	-moz-border-radius-topright : 7px !important;
	-webkit-border-top-right-radius : 7px !important;
	-moz-border-radius-bottomleft : 7px !important;
	-webkit-border-bottom-left-radius : 7px !important;
	-moz-border-radius-bottomright : 7px !important;
	-webkit-border-bottom-right-radius : 7px !important;
	}

/* Header e bottom BORDI ARROTONDATI*/

	#top{
	-moz-border-radius-topleft : 7px !important;
	-webkit-border-top-left-radius : 7px !important;
	-moz-border-radius-topright : 7px !important;
	-webkit-border-top-right-radius : 7px !important;
	}

	#bottom{
	-moz-border-radius-bottomleft : 7px !important;
	-webkit-border-bottom-left-radius : 7px !important;
	-moz-border-radius-bottomright : 7px !important;
	-webkit-border-bottom-right-radius : 7px !important;
	}

/* PAGINA COLORE SFONDO CENTRALE*/

	#top, #header, #content, #bottom, #page{
	background: #ffffff none  no-repeat scroll left top !important;
	}

/* FORUM BOX MESSAGGI */
	.cfWrapper {
	border: 1px solid #333  !important;
	}
	.cfHeader {
	background: url(http://img21.imageshack.us/img21/1723/22264722.png)  !important;
	}

/* TUTTE LE IMMAGINI */
img.scLive, 
img.scTransfer, 
img.scMail, 
img.scNewMail, 
img.scNotes, 
img.scSearch, 
img.scMySupporters, 
img.scReminders, 
img.scBookmarks, 
img.scDigit1, 
img.scDigit2, 
img.scDigit3, 
img.scDigit4,
img.scDigit5, 
img.scChallenges, 
img.scManager, 
img.scMatches, 
img.scPlayers, 
img.scBids, 
img.scFans, 
img.scTraining, 
img.scMatches2, 
img.scEconomy, 
img.f_quote1, 
img.f_quote2, 
img.f_bold, 
img.f_italic, 
img.f_ul, 
img.f_hr, 
img.f_player, 
.bookmarkSmall img, 
img.f_team, 
img.f_match, 
img.f_fed, 
img.f_message, 
img.f_series, 
img.f_www, 
img.hotlist, 
img.matchOrder, 
img.matchOrderSet, 
img.matchOrderSetDisabled, 
img.matchOrderWrite, 
img.matchOrderWriteDisabled, 
img.matchHTLive, 
img.matchFriendly,
img.matchLeague, 
img.matchCup, 
img.matchQualification, 
img.matchMasters, 
img.supporterRed,
img.supporterGreen {

	background-image: url(http://img229.imageshack.us/img229/7977/mastericonscopy.png)   !important;
	}

	#footer a{
        color: #ffffff !important;
	}
	}
