css:
@-moz-document domain("hms.majordomo.ru") {
.main-menu {
    background-color: #222;
}
.main-menu ul ul {
    background-color: #151515;
}
.main-menu li a.active, .main-menu li a:hover {
    background-color: #0055ce;
}
.main-menu li a {
    color: #fff;
}
.main-menu div.icon-account {
    background: url(https://hms.majordomo.ru/images/account-hover.png) center no-repeat;
}
.main-menu div.icon-domains {
    background: url(https://hms.majordomo.ru/images/domains-hover.png) center no-repeat;
}
.main-menu div.icon-balance {
    background: url(https://hms.majordomo.ru/images/balance-hover.png) center no-repeat;
}
.main-menu div.icon-sites {
    background: url(https://hms.majordomo.ru//images/sites-hover.png) center no-repeat;
}
.main-menu ul ul li a.active, .main-menu ul ul li a:hover {
    background-color: #151515;
    color: #0069ff;
}
.main-menu li.expandable > a:after {
    background: url(https://hms.majordomo.ru/images/expand-hover.png) center no-repeat;
}
.main-menu div.icon.svg {
    fill: #f9f9f9;
}
.main-menu div.icon-databases {
    background: url(https://hms.majordomo.ru/images/databases-hover.png) center no-repeat;
}
.main-menu div.icon-mail {
    background: url(https://hms.majordomo.ru/images/mail-hover.png) center no-repeat;
}
.main-menu div.icon-backups {
    background: url("https://hms.majordomo.ru/images/icons/backups-hover.png") center no-repeat;
}
.main-menu div.icon-seo {
    background: url(https://hms.majordomo.ru/images/seo-hover.png) center no-repeat;
}
.main-menu div.icon-constructor {
    background: url(https://hms.majordomo.ru/images/constructor-hover.png) center no-repeat;
}
.main-menu div.icon-access {
    background: url(https://hms.majordomo.ru/images/access-hover.png) center no-repeat;
}
.main-menu div.icon-crontab {
    background: url(https://hms.majordomo.ru/images/icons/crontab-hover.png) center no-repeat;
}
.main-menu div.icon-partner {
    background: url(https://hms.majordomo.ru/images/partner-hover.png) center no-repeat;
}
.main-menu div.icon-actions {
    background: url(https://hms.majordomo.ru/images/actions-hover.png) center no-repeat;
}
header {
    background-color: #0055ce;
}
.account-menu .icon-profile,
.notifications .icon-bell,
.menu-collapse-icon {
    filter: brightness(200%);
}
path#SvgjsPath1007___395930036 {
    fill: #fdfdfd;
}
header .header-balance .big-black {
    color: #fff;
}
header .header-balance {
    color: #00fb24;
}
button.green {
    background-color: #222;
    border-color: #333;
    color: #fff;
}
a#logo,
g#Canvas___-1802044504 {
    fill: #fff;
}
.account-menu .username {
    color: #fff;
}
.account-menu .icon-profile-expand {
    background: url(https://hms.majordomo.ru/images/expand-hover.png) center no-repeat;
}
main .main-block {
    width: auto;
}
main.collapsed .main-block {
    width: auto;
}
button.ghost {
    background-color: #0055ce;
    border-color: #0066f7;
    color: #fff;
    fill: #fdfdfd;
}
.svg-majordomo-name {
    fill: #fff;
}
.mj-blue-button {
    background: #232323;
}
.notification button.ghost {
    background-color: #222;
	 border-color: #222;
}
div > main.collapsed:nth-child(3) > div:last-child > div:first-child > div:first-child > div > div:last-child > div > a > img {
    display: none;
}
}
