}


.kicker-color-icon,
.kicker-color-icon + a,
.title,
.title a,
h1 a{
  color: #c00 !important;
}

.kicker-color-icon,
.kicker-color-icon + a,
.ovMoreItems a,
#vereinsleisteNews .vlNewsContainer h3{
  font-weight: normal !important;
}

.ovMoreItems a,
.kicker-color-icon + a{
  padding-left: .25rem !important;
}

/* header icons */

.ovSocialHeader a{
  box-sizing: border-box;
  height: 22px !important;
  padding: 5px !important;
  border-radius: 50% !important;
}
.ovSocialHeader [class^="icon-"]:before{
  font-family: FontAwesome !important;
  color: #fff;
  font-size: 1rem;
  transition: .3s ease-in-out;
}
.ovSocialHeader [class^="icon-"]:hover:before{
	 color: #333 !important;
}
.ovSocialHeader a{
	background: none !important; 
}


/* SEARCH */

.ovSearchForm{
  background: none !important;
}
#inputsuche{
  background: #fff !important;
  padding-left: .5rem !important;
  box-sizing: border-box; 
  border-radius: .1rem !important;
}
input[type="text"]{
  background: #fff !important;
  border-radius: .1rem !important;
  border-color: #e6e6e6 !important;
  padding: .5rem 5px !important;
  box-sizing: border-box !important;
  vertical-align: middle; 
}
td.suchetext{
  vertical-align: middle !important;
}

.datepicker-opener-table{
  height: 30px !important;
}


.ovSearchBtn{
  background: none !important;
  height: 18px !important;
}
.ovSearchForm{
  position: relative !important;
}
.ovSearchForm::before{
 	font-family: 'kickerfont' !important;
  font-weight: normal;
  font-style: normal; 
  font-size: .8rem;
  content: '\e986';
  color: #fff !important;
  position: absolute !important;
  right: 1rem !important;
  top: .45rem !important;
  width: 20px;
  height: 20px;
  color: #fff !important;
  pointer-events: none !important;
  cursor: pointer !important;
}



/* ARTICLE */

.ovTopmodul,
.ovMoreModul,
.ovWeitereNewsModul,
.ovContextShLeft,
.ovContextShRight,
.ovContextShTop260,
