  width: auto !important;

}

#ovToolsNaviDD > div > span:before,
#ovToolsNaviDD > div > a:before{
  content: '';
  width: 15px; 
  height: 100%;
  position: absolute;
  left: 0;
  top: 0;
  -webkit-filter: invert(100%) brightness(100%);
  filter: invert(100%) brightness(100%);
}
