css:
@-moz-document url-prefix("https://intra.epitech.eu/") {
html { scrollbar-color: rgba(243, 156, 18,1.0) rgba(230, 126, 34,1.0); }

.red.alert, .orange.alert {
    background: url(https://intra.epitech.eu/static9b79e26415eac88edfde7492bf4ff3c09fd52ecd/img/sprite-common.png) no-repeat scroll -140px -73px transparent !important;
}

.msg-alert, .understand-alerts {
    display: none;
}

.info::after {
	content: "Votre temps de log est suppérieur à celui de tous les autres élèves réunis, bravo, vous obtenez votre diplôme dès demain et remplacez Romain Bossa";
}

#header .intra .reserved {
	background: url('https://images.ecosia.org/eyfFtq5G8HdfpSy7Pg-KVg7WjMA=/0x390/smart/http%3A%2F%2Fimagenpng.com%2Fwp-content%2Fuploads%2F2015%2F06%2Fmcdonald-s-psd-457848.png') no-repeat center center !important;
    background-size: 37% !important;
}

.main-wrapper {
	padding-top: 75px;
}

#header {
	position: fixed;
    z-index: 4000;
    width: 100vw;
    top: 0px;
    box-sizing: border-box;
    -moz-box-sizing: border-box !important;
}

.header-wrapper {
	background-color: #f1c40f !important;
}

#notification, .infos {
	background-color: #e67e22 !important;
}

#sidebar {
    background-color: #222f3e !important;
}
}
