css:
@-moz-document domain("coriolis.io")  {


h1 {
	color: #ffbc00 !important; 
}

/* custom scrollbar */
	::-webkit-scrollbar { width: 10px; } /* width */
	::-webkit-scrollbar-track { background: #444; } /* track */
	::-webkit-scrollbar-thumb { background: #888; } /* handle */
	::-webkit-scrollbar-thumb:hover { background: #888; } /* handle hover */

/* TABLES */

table {
    background-color:#000;
    border-spacing:0;
    cursor:default
}
table a, table a:visited {
    color:#ffbc00;
    text-decoration:none
}
thead {
    background-color: #1E557D;
    color: lightgray;
    text-transform:uppercase;
    line-height:1.3em;
    -webkit-touch-callout:none;
    -webkit-user-select:none;
    -khtml-user-select:none;
    -moz-user-select:none;
    -ms-user-select:none;
    user-select:none
}

thead .main th:first-child {
    border-left:none
}
thead th {
    vertical-align:middle;
    font-weight:normal;
    padding:2px .4em 0
}
thead th.lft {
    border-left: 1px solid #000;
}
thead th.rgt {
    border-right: 1px solid #000;
}
thead th.invisible {
    opacity: 0;
}
tbody tr.tr {
    color:#CCC;
    text-align:right
}
.no-touch tbody tr.highlight:hover, .no-touch tbody tr.highlighted {
    background-color:#1E557D;
    color: #FFF;
}

tbody tr:nth-child(even) {
    background-color: #262626;
}
tbody tr:nth-child(odd) {
    background-color: #202020;
}



td {
    line-height:1.4em;
    padding:0 .3em
}
td.val {
    border:1px solid #1E557D
}
td.lbl {
    border:1px solid #1E557D;
    text-transform:uppercase;
    color:#000;
    background-color:#3189C9
}
td.tl {
    text-align:left;
    padding-left:.7em
}
td.tc {
    text-align:center
}

.fg {
    color:#CCC;
    stroke:#CCC;
    fill:#CCC;
}
.muted {
    color:#999;
    stroke:#999;
    fill:#999;
}
.disabled {
    color:#555;
    stroke:#555;
    fill:#555;
}
.primary {
    color:#3189C9;
    stroke:#3189C9;
    fill:#3189C9;
}
.primary-bg {
    color:#000;
    stroke:#000;
    fill:#000
}
.primary-disabled {
    color:#3189C9;
    stroke:#64A8D4;
    fill:#64A8D4
}
.secondary {
    color:#3189C9;
    stroke:#1E557D;
    fill:#1E557D;
}
.secondary-disabled {
    color:#0088d2;
    stroke:#0088d2;
    fill:#0088d2
}
.warning {
    color:#d72130;
    stroke:#d72130;
    fill:#d72130
}
.warning-disabled {
    color:#b32900;
    stroke:#b32900;
    fill:#b32900
}
.bg-warning-disabled {
    background-color:#b32900
}
 
.icon {
    display:inline-block;
    vertical-align:middle;
    width:1.1em;
    height:1em;
	fill: #e69500;
	stroke: #e69500;
}
.icon.sm {
    width:.8em;
    height:.75em
}
.icon.tn {
    width:.6em;
    height:.5em
}
.icon.lg {
    width:1.6em;
    height:1.5em
}
.icon.xl {
    width:2.1em;
    height:2em
}
.modicon {
    display:inline-block;
    vertical-align:middle;
    width:1.1em;
    height:1em;
    stroke-width:20;
    fill:transparent
}
.modicon.sm {
    width:.8em;
    height:.75em
}
.modicon.tn {
    width:.6em;
    height:.5em
}
.modicon.lg {
    width:1.6em;
    height:1.5em
}
.modicon.xl {
    width:2.1em;
    height:2em
}
.summary {
    stroke:#CCC;
    fill:#CCC
}
.summary svg {
    stroke-width:10
}
#app-update {
    position:fixed;
    top:0;
    left:0;
    right:0;
    height:3em;
    z-index:3;
    line-height:3em;
    text-align:center;
    background-color:#1e1e1e;
    color:#d72130;
    cursor:pointer
}
.view-changes {
    position:fixed;
    top:3em;
    left:0;
    right:0;
    height:3em;
    z-index:3;
    line-height:3em;
    text-align:center;
    background-color:#1e1e1e;
    color:#d72130;
    cursor:pointer
}
header {
    background-color:#1e1e1e;
    margin:0;
    padding:0 0 0 1em;
    height:3em;
    line-height:3em;
    font-family:'Eurostile', Arial, sans-serif;
    vertical-align:middle;
    position:relative;
    z-index:2;
    box-sizing:border-box;
    -webkit-touch-callout:none;
    -webkit-user-select:none;
    -khtml-user-select:none;
    -moz-user-select:none;
    -ms-user-select:none;
    user-select:none
}
header .menu {
    position:relative;
    z-index:1;
    cursor:default
}
header .menu.r .menu-list {
    right:0
}

header .menu-header {
    height:100%;
    z-index:2;
    padding:0 1em;
    cursor:pointer;
    color:#ffbc00;
    text-transform:uppercase
}
header .menu-header:hover {
 	color: #FFF;   
}
header .menu-header.disabled {
    color:#4d2600;
    cursor:default
}
header .menu-header.selected {
    background-color:#000
}
header .menu-header .menu-item-label {
    margin-left:1em;
    display:inline-block
}

header .menu-list {
    font-family:'Eurostile', Helvetica, sans-serif;
    position:absolute;
    padding:.5em 1em;
    box-sizing:border-box;
    min-width:100%;
    overflow-x:hidden;
    background-color:#000;
    font-size:.9em;
    overflow-y:auto;
    z-index:0;
    -webkit-overflow-scrolling:touch;
    max-height:500px
}
header .menu-list::-webkit-scrollbar {
    width:.5em
}
header .menu-list::-webkit-scrollbar-track {
    background-color:transparent
}
header .menu-list::-webkit-scrollbar-thumb {
    background-color:#b32900
}
header .menu-list input {
    border:none;
    background-color:transparent;
    text-align:right;
    font-size:1em;
    font-family:'Eurostile', Helvetica, sans-serif
}

header .dbl {
    -webkit-column-count:2;
    -moz-column-count:2;
    column-count:2
}
header .dbl ul {
    min-width:10em
}

header ul {
    display:inline-block;
    white-space:nowrap;
    margin:0 0 .5em;
    padding:0;
    line-height:1.3em
}
header li {
    white-space:normal;
    list-style:none;
    margin-left:1em;
    line-height:1.1em
}
header a {
    vertical-align:middle;
    color:#ffbc00;
    text-decoration:none
}
header a:visited {
    color:#ffbc00
}
.no-touch header a:hover {
    color:#fff;
}
header a.active {
    color:#ffbc00
}
header hr {
    border:none;
    border-top:1px solid #555
}
header .no-wrap {
    overflow-x:auto;
    white-space:nowrap
}
header .block {
    display:block;
    line-height:1.5em
}
header .title {
    font-size:1.3em;
    display:inline-block;
    margin:0px;
    text-transform:uppercase
}
#shipyard {
    vertical-align:top;
    text-align:center
}
a.ship {
    display:inline-block;
    width:15em;
    margin:.5em;
    background-color:#1e1e1e;
    padding:.5em;
    text-decoration:none;
    color:#CCC;
    font-family:'Eurostile', Helvetica, sans-serif;
    text-align:right;
    -webkit-touch-callout:none;
    -webkit-user-select:none;
    -khtml-user-select:none;
    -moz-user-select:none;
    -ms-user-select:none;
    user-select:none
}

a.ship h2 {
    width:100%;
    margin:0;
    text-align:left;
    font-size:1.1em
}
a.ship small {
    color:#d72130;
    font-family:'Eurostile', Helvetica, sans-serif;
    float:left;
    font-size:.8em;
    margin-right:2em
}
a.ship .subtitle {
    overflow:hidden;
    margin-bottom:20px
}
a.ship .size {
    float:left;
    font-size:.8em
}
a.ship .purpose {
    text-transform:uppercase;
    font-size:.7em;
    float:right
}
.items {
    margin-bottom:10px;
    clear:both;
    overflow:hidden
}
.items .item {
    clear:both;
    margin:1px 0 0;
    overflow:hidden;
    color:#555;
    cursor:pointer
}
.items .item .val {
    float:right;
    text-align:right
}
.items .item .lbl {
    float:left
}
.items .item.enabled {
    color:#CCC
}
.items .item.consumer .val:before {
    content:"-"
}
.items .item.untoggleable {
    cursor:default
}
.slot {
    float:left;
    text-align:left;
    width:100%;
    margin-top:.25em;
    position:relative;
    z-index:0;
    padding-right:.4em;
    padding-left:1.65em;
    box-sizing:border-box;
    background-color:#000;
    border:1px solid #1E557D;
    color:#CCC;
    stroke:#CCC;
    fill:#CCC
}
.slot .details-container {
    min-height:2.7em;
    padding:.25em 0;
    box-sizing:border-box;
    overflow:hidden;
    height:100%;
    cursor:pointer;
    text-transform:none
}
.slot .details {
    min-height:2.2em;
    background-color:#000
}
.slot .name {
    overflow:hidden;
    white-space:nowrap;
    max-width:80%;
    text-overflow:ellipsis
}
.slot .modification-container td {
    width:25%;
    text-align:center
}
.slot .modification-container .input-container {
    width:75%;
    text-align:right
}
.slot .modification-container input {
    width:80%
}
.slot .modification-container .unit-container {
    width:30px;
    padding:3px;
    text-align:left;
    display:inline-block
}
.slot .modification-container .header-adjuster {
    width:25%;
    display:inline-block
}
.slot .cb {
    overflow:hidden
}
.slot input.cb:focus {
    border-color:#fff
}
.slot input.greyed-out {
    border-color:#888;
    color:#888
}
.slot .l {
    text-transform:capitalize;
    margin-right:.8em;
}
.slot .sz {
    text-align:center;
    position:absolute;
    top:0;
    height:100%;
    left:0;
    font-size:1.2em;
    width:1.2em;
    color:#3189C9;
    stroke:#1E557D;
    border-right:1px solid #1E557D;
    box-sizing:border-box;
    padding-top:.2em;
    padding-left:.05em
}
.slot .sz svg {
    stroke-width:20
}
.slot .empty {
    text-transform:uppercase;
    font-size:1.3em;
    color:#1E557D;
    stroke:#1E557D;
    text-align:center;
    letter-spacing:.1em;
    line-height:1.7em
}
.slot .empty svg {
    stroke-width:20
}
.slot.selected {
    color:#FFF;
    stroke:#000;
    fill:#000;
    background-color:#3189C9;
    border:1px solid #3189C9;
    z-index:1
}
.slot.selected .sz {
    color:#3189C9;
    stroke:#3189C9;
    background-color:#000;
    border-right:1px solid #3189C9
}
.slot.selected .sz svg {
    stroke-width:20
}
.slot.selected .details {
    background-color:transparent
}
.slot.selected svg {
    stroke-width:20
}
.slot.eligible {
    border:1px solid #0088d2
}
.slot.eligible .sz {
    color:#0088d2;
    border-right:1px solid #0088d2
}
.slot.eligible .details {
    background-color:transparent
}
.slot.ineligible {
    cursor:no-drop;
    color:#555;
    fill:#555;
    border:1px solid #555
}
.slot.ineligible .sz {
    color:#555;
    border-right:1px solid #555
}
.slot.dropEmpty {
    color:#b32900;
    fill:#b32900;
    border:1px solid #b32900
}
.slot.dropEmpty .sz {
    color:#b32900;
    border-right:1px solid #b32900
}
.slot.dropEmpty .details {
    background-color:transparent
}
.slot.drop {
    color:rgba(0, 53, 82, 0.9);
    fill:rgba(0, 53, 82, 0.9);
    border:1px solid #1FB0FF;
    background-color:#0088d2
}
.slot.drop .sz {
    color:#1FB0FF;
    background-color:#000;
    border-right:1px solid #1FB0FF
}
.slot.drop .details {
    background-color:transparent
}
.slot svg {
    stroke-width:20
}
#outfit {
    max-width:1600px;
    margin:0 auto;
    -webkit-touch-callout:none;
    -webkit-user-select:none;
    -khtml-user-select:none;
    -moz-user-select:none;
    -ms-user-select:none;
    user-select:none;
    font-size:.9em
}

#outfit table {
    white-space:nowrap
}
#overview {
    overflow:hidden;
    margin:.5em
}
#overview h1 {
    margin:0 1em .2em 0;
    float:left
}
#requirements {
    margin:0;
    float:left;
    text-align:left
}
#requirements div {
    display:inline-block;
    margin:0 1em .5em 0;
    height:1.6em;
    line-height:1.6em;
    text-align:left;
    vertical-align:middle;
    cursor:default;
    background-repeat:no-repeat;
    background-position:left center;
    background-size:contain
}

#summary {
    overflow-x:auto;
    -webkit-overflow-scrolling:touch;
    box-sizing:border-box;
    width:100%;
    padding:.5em .2em;
    font-size:.9em
}
#summary .summaryTable {
    -webkit-touch-callout:none;
    -webkit-user-select:none;
    -khtml-user-select:none;
    -moz-user-select:none;
    -ms-user-select:none;
    user-select:none;
    width:100%;
    border-collapse:collapse
}
#summary .summaryTable>thead.blue {
    background-color:#3189C9 !important;
    border-left:1px solid #000;
    color:#fff;
}
#summary .summaryTable>thead.green {
    background-color:#71a052;
    border-left:1px solid #000;
    color:#000
}
#summary .summaryTable>thead.purple {
    background-color:#9274c4;
    border-left:1px solid #000;
    color:#000
}
#summary .summaryTable thead th.bordered {
    border-left:1px solid #000
}
.flip {
    display:inline-block;
    -moz-transform:scaleX(-1);
    -o-transform:scaleX(-1);
    -webkit-transform:scaleX(-1);
    transform:scaleX(-1)
}
.section-menu {
    position:relative;
    z-index:0
}
.section-menu.selected {
    z-index:1
}
.section-menu.selected h1 {
    background-color:#3189C9
}
.section-menu h1 {
    cursor:pointer
}
.section-menu h1 .icon {
    float:right;
    margin:.1em .3em 0 0
}
.section-menu .select {
    box-sizing:border-box;
    left:0
}
#build {
    float:right;
    line-height:2em;
    text-align:left
}
#build input {
    background:none;
    line-height:1.3em;
    width:15em;
    font-size:.9em;
    box-sizing:border-box;
    display:inline-block;
    padding:.3em;
    vertical-align:middle
}
#build input:focus {
    border:1px solid #3189C9;
    color:#3189C9
}

.shorten {
    overflow:hidden;
    max-width:8em;
    text-overflow:ellipsis
}

.tabs {
    width:100%;
    box-sizing:border-box;
    margin-bottom:1px
}
.tabs, .tabs th {
    cursor:pointer;
    border-collapse:collapse;
    color:#3189C9;
    background-color:#000;
    border:1px solid #1E557D;
    padding-top:1px
}
.tabs .active {
    color:#FFF;
    background-color:#3189C9
}
.group {
    width:25%;
    padding:.5em .2em;
    vertical-align:top;
    box-sizing:border-box;
    display:inline-block;
    -webkit-touch-callout:none;
    -webkit-user-select:none;
    -khtml-user-select:none;
    -moz-user-select:none;
    -ms-user-select:none;
    user-select:none;
    cursor:default
}
.group h1 {
    font-family:'Eurostile', Helvetica, sans-serif;
    color:lightgray;
    background-color:#1E557D;
    text-transform:uppercase;
    margin:0;
    padding-top:2px;
    font-size:1em;
    line-height:1.3em;
    font-weight:normal
}

.group.quarter {
    width:25%
}

.group.third {
    width:33%
}

.group.twothirds {
    width:67%
}

.group.threequarters {
    width:75%
}

.group.full {
    width:100%
}

.power-band text, .power-band rect {
    cursor:pointer
}
.power-band rect {
    stroke-width:1px;
    stroke:#000
}
.threshold {
    stroke:#0088d2 !important;
    fill:#0088d2 !important
}
.threshold.exceeded {
    stroke:#ffbc00 !important;
    fill:#ffbc00 !important
}

#comparison {
    display:inline-block;
    text-align:left;
    border-collapse:collapse;
    text-transform:uppercase;
    font-size:.8em
}
#comparison td {
    border:1px solid #1E557D;
    padding:0
}
#comparison td h3 {
    font-size:1.1em;
    margin:0 1em;
    line-height:2em;
    display:inline-block
}

#comparison input {
    background:none;
    line-height:1.3em;
    width:20em;
    height:100%;
    display:inline-block;
    padding:.3em;
    font-size:1em;
    vertical-align:middle;
    border:none;
    border-right:1px solid #1E557D
}

#comparison .head {
    background-color:#3189C9;
    color:#000;
    padding:0 .5em;
    text-align:right
}
#facet-container {
    display:block;
    -webkit-touch-callout:none;
    -webkit-user-select:none;
    -khtml-user-select:none;
    -moz-user-select:none;
    -ms-user-select:none;
    user-select:none;
    cursor:default;
    white-space:nowrap;
    overflow:hidden;
    margin:0;
    padding:0 1px
}
#facet-container .as-sortable-placeholder {
    background-color:#000
}
#facet-container .as-sortable-dragging {
    cursor:ew-resize
}
.facet-placeholder {
    display:inline-block;
    background-color:#000
}
.facet {
    cursor:pointer;
    display:inline-block;
    background-color:#000;
    margin:0;
    padding:0 .5em;
    line-height:2.5em;
    list-style:none;
    white-space:nowrap;
    color:#555
}
.facet.active {
    color:#d72130;
    background-color:rgba(51, 12, 0, 0.9)
}
.facet.active svg {
    fill:#d72130
}
#build-select {
    clear:both;
    margin:1em 0 0;
    overflow-y:auto;
    text-align:center
}
#build-select h1 {
    display:inline-block;
    margin:0 .7em;
    vertical-align:top
}
#build-select .build-section {
    box-sizing:border-box;
    display:inline-block;
    width:45%
}

#build-select .build-section h1 {
    box-sizing:border-box;
    margin:0;
    width:100%;
    font-size:1em;
    color:#000;
    padding:2px .4em 0;
    background-color:#3189C9;
    text-transform:uppercase;
    line-height:1.3em;
    font-weight:normal
}
#build-select .build-section div {
    display:block;
    font-size:.8em;
    width:100%;
    overflow-y:auto;
    z-index:0;
    -webkit-overflow-scrolling:touch;
    height:8em;
    max-height:8em
}
#build-select .build-section table {
    width:100%
}
#build-select .build-section td {
    cursor:pointer;
    vertical-align:top;
    border-bottom:1px solid #1E557D
}
#comp-tbl {
    margin:1em auto;
    white-space:nowrap;
    font-size:.8em
}
#comp-tbl td {
    padding:0 .5em
}


select {
    -webkit-border-radius:0;
    -moz-border-radius:0;
    border-radius:0;
    -moz-background-clip:padding;
    -webkit-background-clip:padding-box;
    background-clip:padding-box;
    cursor:pointer;
    background:none;
    color:#3189C9;
    font-family:'Eurostile', Helvetica, sans-serif;
    font-size:1em;
    background-color:transparent;
    -webkit-appearance:none;
    -moz-appearance:none;
    appearance:none;
    padding:0 .5em;
    outline:none;
    border:0
}
select:focus {
    outline:none
}
select::-moz-focus-inner {
    border:0
}
.react-fuzzy-search>* {
    padding:0 !important;
    color:#3189C9
}
.react-fuzzy-search>*>input {
    border:1px solid #3189C9 !important;
    color:#000
}
.cmdr-select {
    border:1px solid #3189C9;
    padding:.5em .5em
}
.select {
    color:#3189C9;
    position:absolute;
    left:-1px;
    padding:.5em 0;
    width:100%;
    margin:0;
    max-height:500px;
    overflow-y:auto;
    overflow-x:hidden;
    z-index:0;
    -webkit-overflow-scrolling:touch;
    background-color:#1e1e1e;
    border:1px solid #3189C9;
    text-align:center
}

.select::-webkit-scrollbar {
    width:.5em
}
.select::-webkit-scrollbar-track {
    background-color:transparent;
    border-left:1px solid #3189C9
}
.select::-webkit-scrollbar-thumb {
    background-color:#3189C9
}
.select .select-category {
    color: #ffbc00;
    white-space:nowrap;
    line-height:2em;
    font-size:1.2em;
    text-align:center;
    margin:.5em 0;
    padding-left:5px;
    border-top:3px solid #3189C9;
    border-bottom:0px solid #1E557D;
    overflow:hidden;
    text-overflow:ellipsis
}
.select .select-group {
