css:
@-moz-document domain("docutils.sourceforge.net") {
body {
    background-color: #f5f7f9;
}
}

@-moz-document domain("docutils.sourceforge.net") {
a {
    color: #666666;
}
a:hover {
    color: #fc6427;
}
}

@-moz-document domain("docutils.sourceforge.net") {
h1 {
    font-size: 250%;
}

h2 {
    font-size: 200%;
}

h3 {
    font-size: 180%;
}

h4 {
    font-size: 140%;
}

h5,
h6 {
    font-size: 120%;
}
}

@-moz-document domain("docutils.sourceforge.net") {
.document {
    color: #565656;
    background-color: #ffffffaa;
    font-size: 16px;
    min-width: 600px;
    max-width: 1000px;
    margin-top: 2em;
    margin-left: auto;
    margin-right: auto;
    padding-top: 4em;
    padding-left: 2em;
    padding-right: 2em;
    padding-bottom: 4em;
}
}

@-moz-document domain("docutils.sourceforge.net") {
.literal, .literal-block {
    font-family: Consolas, monospace;
    background-color: #f1f1f1;
}

.literal-block {
    padding: 1em;
}

.note {
    background-color: #eef1f3;
    border: none !important;
}

.admonition-title {
    font-size: 140%;
}
}
