css:
@-moz-document domain(feedly.com) {
#feedlyPageHolderFX {
	padding-top: 0px;
}
.feedlyChrome--leftnav-closed #feedlyFrame {
	margin-left: 0px;
	margin-top: 56px;
	z-index: 502;
}
.feedlyChrome--leftnav-peek #feedlyFrame {
	margin-top: 56px;
}
.sliderWidth {
	width: auto;
}
.floatingEntryContentSlide {
	margin: 0 5%;
}
.slideEntryContent img {
	max-width: 100% !important;
}
.fx .modal.small {
	width: auto
}
.fx .modal {
	min-width: 0;
}
.fx .entry.u4 .title {
	width: 100%;
}
.pro,
.Leftnav__dock > div:nth-child(1) > button:nth-child(1),
.fx .entry.u4 .save-for-later,
.fx .entry.u4 .mark-as-read,
.fx .entry.u4 .LessLikeThisSelector__button--entry-u4,
.fx .entry.u4 .hide,
.fx .entry.u4 .tag-button {
	display: none;
}
.entryBody iframe {
	width: 100% !important;
}
}
