css:
@-moz-document domain(192.168.1.1)
{
  /* Login screen */
  body.LOGIN_L {
      background-color: #333;
      color: #eee;
  }
  #oINNER {
      background-color: #444;
      color: #eee;
  }
  #MAIN_PAINT.table.tr.td {
      background-color: #d88;   
  }
  INPUT.TEXT_ENABLE {
      color: #fff;
      background-color: #333;
  }
  #btn_logon, #btn_clear {
      background-color: #333;
      font-weight: 700;
  }

  /* Tabs */
  /* Reset the tabs */
      TD.TAB_SELECT,
      TD.TAB_SELECT_NORMAL,
      TD.TAB_NO_SELECT,
      TD.TAB_NO_SELECT_NORMAL,
      TD.L_WH, TD.R_WH,
      TD.L_RH, TD.R_RH,
      TD.L_BL, TD.R_BL,
      TD.L_WH_R_BL, 
      TD.L_BL_R_BL,
      TD.L_BL_R_WH
      {
      background: none;
      padding: 0;
      width: 0;
  }

  /* Tab spacing */
  TD.TAB_SELECT, TD.TAB_SELECT_NORMAL {
      padding: 0 10px 0 10px;
  }
  TD.TAB_NO_SELECT, TD.TAB_NO_SELECT_NORMAL{
      padding: 0 10px 0 5px;
  }

  /* Tab coloring */
  TD.L_BL, TD.L_BL_R_WH, TD.L_BL_R_BL, TD.L_WH_R_BL {
      width: 5px;
      background-color: #888;
  }

  #tr_tab {
      background-color: #666;
      font-weight: 900;
  }
  TD.TAB_NO_SELECT, TD.L_BL_R_BL, TD.TAB_NO_SELECT_NORMAL, TD.R_BL {
      background-color: #888;
      font-weight: 600;
  }
  SPAN.TAB {
      color: #ddd;
  }
  TD.TAB_SELECT DIV.TAB_DIV SPAN.TAB {
      color: #fff;
      font-weight: 700;
  }

  /* Single tab */
  TABLE.TAB {
      background-color: #666;
  }
  #td_tab_1 {
      padding: 0 10px 0 5px;
      font-weight: 900;
  }



  /* Left menu */
  menu, DIV.BODY {
      background-color: #666;
      border-left: 10px solid #666;
      color: #fff;
  }
  ol.dot2Click {
      background-color: #777;
      color: #fff;
  }
  #bottomLeftFrame html body {
      background-color: #f66;
  }
  SPAN.PRIMARY_MENU, SPAN.SECONDARY_MENU, SPAN.SPAN_LOGOUT {
      color: #fff;
  }
  SPAN.SPAN_LOGOUT {
      color: #fff;
  }
  #t_corporation, SPAN.SPAN_COPYRIGHT {
      color: #aaa;
  }


  /* Main Pages */
  BODY.MAIN {
      background-color: #333;
      color: #eee;
  }

  /*  */
  DIV.SECONDARY_TITLE {
      background-color: #777;
  }
  SPAN.SECONDARY_TITLE {
      color: #fff;
      font-weight: 900;
      font-size: 11pt;
      top: 2px;
  }
  INPUT.SELECTED, INPUT.TEXT_DISABLE {
      background-color: #666;
      color: #eee;
      font-weight: 600;
  }


  /* Buttons */
  SPAN.BTN input, SPAN.BTN_HOVER input {
      background-image: none;
      background: none;
      margin: 0;
      font-weight: 600;
  }
  SPAN.BTN input {
      background-color: #666;
  }
  SPAN.BTN_HOVER input  {
      background-color: #777;
  }

  /* Text Items */
  SPAN {
      color: #fff;
  }
  DIV.IF_DESCRI {
      background-color: #777;
      color: #fff;
  }


  /* Tables */
  TR.TABLE_HEAD {
      background-color: #222;
      font-weight: 700;
      color: #fff;
  }
  TD.NORMAL {
      background-color: #222;
  }
  TR.TABLE_ENTRY_NORMAL:hover td {
      background-color: #333;
  }
}
