css:
@-moz-document domain(nextdoor.com)
{
  body, .body {
        background-color: #333;
    }

  .header-bar-global-search-input-submit {
      background-color: #222;
  }

    .post-container,
    .classifieds-single-item-content {
        background-color: #777;
        color: #fff;
    }
    .comment-container,
    .card-container,
    .create-post-prompt-container
    {
        background-color: #555;
        color: #fff;
    }

    .content-title {
        color: #fff;
    }

    .post-action-comment-text,
    .thank-button-text {
        color: #046;
    }

  .avatar-link {
      background-color: #046;
      color: #000;
  }
  .avatar-image {
      content: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAABCAQAAAC1HAwCAAAAC0lEQVR42mNkqAcAAIUAgUW0RjgAAAAASUVORK5CYII=);
      position: relative;
      overflow: hidden;
      width: 24px;
      height: 24px;
      padding: 3px 3px 3px 3px;
      box-sizing: border-box;
  }

    .comment-thank-count {
        color: #232;
    }

    a,
    .comment-detail-author-name,
    .see-previous-comments-link,
    .author-name {
        color: #eee;
    }
    a:hover {
        color: #ddf;
    }

    .comment-byline-cursor {
        color: #ddd;
    }

    .comment-detail-scopeline-timestamp {
        color: #aaa;
    }


  input {
      background-color: #222;
  }

  .add-reply-text-input-prompt input,
  .comment-add-reply-text-input input {
      background-color: #222;
  }

  .comment-add-reply-action-bar,
  .comment-add-reply-action-bar-contents {
      background-color: #222;
  }

  .nav-bar-list-item-active {
      color: #fff !important;
  }

  .nav-bar-section-title {
      color: #aaa;
  }

    input.navbar-left,
    .create-post-prompt-text-input,
    .comment-add-reply-text-input,
    .tagging-flow-search-input
    {
        background-color: #333;
        color: #fff;
    }

    .nav-menu-item-link {
      color: #eee;
      background-color: #333;
    }


    .js-media-post,
    .channels-carousel-promo,
    .static-promo-container,
    .promo-container {
        display: none;
    }
}
