css:
@-moz-document domain("gamingonlinux.com") {
.mw-body, .parsoid-body, #comments-background, #body
{
    background-color: #1A1A1A !important;
    color: #777 !important;
}


pre, .mw-code, code, table.wikitable, #footer ul li, .suggestions-special .special-label, .suggestions-special .special-query, h1, h2, h3, h4, h5, h6, .tocnumber, table tr th, .button, button, input[type="submit"], input[type="reset"], input[type="button"], div.announce, a:link, a:visited
{
    color: #777 !important;
}

.badge, .badge a:link, .badge a:visited, .comments .head a, .featured-title, .forum-badge a, #footer, #footer #about a, #navigation-mobile, .head, .head a{
    color: #AAA !important;
}


table.wikitable > tr > th, table.wikitable > * > tr > th, table.wikitable, .suggestions-special, .suggestions-results, .toc, .mw-warning, .toccolours, .fancybox-slide > *, .post_row, .comment, .box .body, .article .content{
    background-color: #2C2C2C !important;
}

.fancybox-close-small::after{
    background: none !important;
}

#simpleSearch{
    background-image: none !important;
    transition: none !important;
}

.login-box, .fancybox-close-small,.article{
    background-color: transparent !important;
}

.vectorTabs, .vectorTabs span, body, #mw-page-base{
    background: none !important;
    background-color: #2C2C2C !important;
}

.mw-body{
    border-color: #2C2C2C !important;
}

.likes{
    background: none !important;
    background-color: #2C2C2C !important;    
    border-left: 2px solid #2C2C2C !important;
}

.article_likes{
    background: none !important;
    background-color: #2C2C2C !important;    
}

#simpleSearch, .comment .comment-meta, .article .tags ul li{
    background-color: #1F1F1F !important;
}

.article.individual.no-image .info{
    background-color: transparent !important;
    filter: invert(0.87) !important;
}

.collapse_container .collapse_header, .hidden_video{
    background-color: #1F1F1F !important;
    color: #777 !important;
}

.table-stripes-tbody tbody:nth-child(2n), .button, button, input[type="submit"], input[type="reset"], input[type="button"], .table-stripes tr:nth-child(2n), .box .subhead{
    background-color: #1F1F1F !important;
}

.btn-auth, input, textarea, select{
    background-color: #1F1F1F !important;
    color: #777 !important;
}

.badge.blue{
    background-color: #111 !important;
}

.patreon_comments > div > img, div.announce > a > img{
    filter: grayscale(1) !important;
}

#simpleSearch, .button, button, input[type="submit"], input[type="reset"], input[type="button"], select.pagination, ul.pagination li a{
    box-shadow: none !important; text-shadow: none !important;
}

.table-stripes-tbody tbody:nth-child(2n+1), .badge.yellow, .breadcrumb, .table-stripes tr:nth-child(2n+1), select.pagination, ul.pagination li a{
    background: #333 !important;
}

pre, .mw-code, code, .bundle-row:hover{
    background: #222 !important;
}

.login-box li a{
    margin-left: 5px;
    margin-right: 5px;
    background-color: #2C2C2C !important;
    border: 1px solid #555 !important;
}

pre, .mw-code, code, .suggestions-results, table tr th, .collapse_container, hr, .patreon_comments, .btn-auth, .comment, .button, button, input[type="submit"], input[type="reset"], input[type="button"], select.pagination, ul.pagination li a, .article .info .desc, .box .body.list ul li, input, textarea, select{
    border-color: #777 !important;
}

.badge.orange{
    background: #444 !important;
}

.comment .comment-meta .comment-actions li a{
    filter: invert(0.75) !important;
}

.comment img.avatar{
    filter: brightness(0.7) !important;
}

.article.individual .info{
    margin-bottom: 0px !important;
}

a:link{
    text-decoration: underline;
}

.comments-pip > a, .quick-tags > a, .comment-post > a, .subhead > a, li.dropdown> ul > li > a, .who-likes > a,.group > a, a.btn-auth, .meta > strong > a, .title > a, article .info .title, .login-box li a, .header-navbar > li > a{
    text-decoration: none;
}

g > text, svg > text, .golsvg_counters, div.post_row:nth-child(9) > div:nth-child(2) > div:nth-child(1) > span:nth-child(1) > img:nth-child(3){
    fill: #777 !important;
}

svg > rect{
    fill:#1F1F1F!important;
}
}
