css:
@namespace url(http://www.w3.org/1999/xhtml);
body > #hatebu_content,
body > #hatebu_content *{

  font-size:13px!important;

  background-color:transparent!important;
  background-image:none!important;
  border:none!important;
  bottom:auto!important;
  color:white!important;
  float:none!important;
  font-style:normal!important;
  font-weight:normal!important;
  height:auto!important;
  left:auto!important;
  letter-spacing:normal!important;
  line-height:1.2em!important;
  margin:0!important;
  max-height:none!important;
  max-width:none!important;
  min-height:0!important;
  min-width:0!important;
  opacity:1!important;
  padding:0!important;
  position:static!important;
  right:auto!important;
  text-align:left!important;
  text-decoration:none!important;
  top:auto!important;
  width:auto!important;
}

body > #hatebu_content{
position:fixed!important;
  background-color:transparent!important;
  border-style:solid!important;
  border-width:7px 0 0 7px!important;
  bottom:0!important;
  display:block!important;
  max-height:430px!important;
  -moz-border-left-colors:rgba(0,0,0,0.03) rgba(0,0,0,0.06) rgba(0,0,0,0.09) rgba(0,0,0,0.14) rgba(0,0,0,0.21) rgba(0,0,0,0.3) rgba(0,0,0,0.4)!important;
  -moz-border-radius-topleft:7px!important;
  -moz-border-top-colors:rgba(0,0,0,0.03) rgba(0,0,0,0.06) rgba(0,0,0,0.09) rgba(0,0,0,0.14) rgba(0,0,0,0.21) rgba(0,0,0,0.3) rgba(0,0,0,0.4)!important;
  opacity:1!important;
  overflow-x:hidden!important;
  overflow-y:auto!important;
  right:0!important;
  z-index:100000000!important;
}

body > #hatebu_content > #hatebu > table{
  border-spacing:2px!important;
  margin-bottom:4px!important;
  table-layout:auto!important;
  width:780px!important;
}
body > #hatebu_content:not(:hover) > #hatebu > table,
body > #hatebu_content:not(:hover) > #hatebu{
  display:none!important;
}

body > #hatebu_content > #hatebu_status{
  background-color:rgba(0,0,0,0.83)!important;
  padding:4px 16px!important;
  text-align:right!important;
}
body > #hatebu_content > #hatebu_status:only-child{
  background-color:rgba(0,0,0,0.5)!important;
}
body > #hatebu_content > #hatebu{
  background-color:rgba(0,0,0,0.83)!important;
  padding:0 32px!important;
}
body > #hatebu_content > #hatebu_status:empty{
  padding:8px 16px!important;
}
body > #hatebu_content > #hatebu:empty{
  padding:0!important;
}

body > #hatebu_content > #hatebu > table > tr > td{
  padding-bottom:3px!important;
}
body > #hatebu_content > #hatebu > table > tr > td:first-child{
  color:#8FA6F1!important;
  font-weight:bold!important;
  padding-right:3px!important;
  text-align:right!important;
  width:60px!important;
}
body > #hatebu_content > #hatebu > table > tr > td:last-child{
  max-width:688px!important;
}

body > #hatebu_content + #hatebu_content,
html:root > head:empty + body > pre:first-child + #hatebu_content:last-child{
  display:none!important;
}
