css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(civ-wiki.vimeworld.ru)
{

  *
  {
    background: #252525 !important;
    border-color: #252525 !important;
    color: #9a9a9a !important;
  }

  #p-logo a
  {
    background-position: center center !important;
    background-repeat: no-repeat !important;
  }

  a
  {
    color: #3296c8 !important;
  }

  a:visited
  {
    color: #d08132 !important;
  }

  a:link
  {
    color: #d08132 !important;
    font-weight: bold !important;
  }

  a:hover, a:hover span
  {
    color: #3296c8 !important;
    text-decoration: underline !important;
  }

  #content, #content *
  {
    background: #202020 !important;
    border-color: #555 !important;
  }

  #content
  {
    -moz-border-radius: 10px;
    -webkit-border-radius: 10px;
    border-radius: 10px;
  }

  #toc, #toc *
  {
    background: #252525 !important;
    border-color: #252525 !important;
  }

  #toc
  {
    -moz-border-radius: 10px;
    -webkit-border-radius: 10px;
    border-radius: 10px;
  }

  #p-cactions li.selected a, #p-cactions li a, #p-cactions li a:hover
  {
    padding: 0 1em 0.1em !important;
  }

  #bodyContent pre
  {
    background: #1a1a1a !important;
  }

/************** IMAGES **************/


/************** IMAGES **************/


#p-logo > a.mw-wiki-logo
{
  background-image: none;
  background: url(https://i.imgur.com/y3Kar1P.png) center 3px !important;
  background-size: cover !important;
  width: 157px !important;
  height: 160px !important;
  background-repeat: no-repeat !important;
  margin-top: 2px !important;
}


/** BLANKED OUT IMAGES **/


  img[src*="/images/poweredby_mediawiki_88x31.png"], img[src*="/images/wikimedia-button.png"], img[src*="/Bookshelf-40x201_6.png"]
  {
    width: 0px !important;
    background: none !important;
  }

  .portlet li
  {
    list-style-image: none !important;
  }

  ul
  {
    list-style-image: none !important;
  }
}
