css:
@-moz-document regexp("(http|https)://(www.)*google.\\S+/$") {

/* logo */

#hplogo {
  width: 639px;
  height: 0;
  background-image: url(https://i.imgur.com/79OASNs.png);
  padding-top: 254px !important;
  background-size: cover;
  background-position: center;
}

/* space things out for bigger logo */

.A8SBwf {
  margin-top: 50px;
}

#prm-pt {
  margin-top: 50px !important;
}

/* search bar background color and shadows */

.RNNXgb {
  box-shadow: none;
  background-color: #F1F3F4;
}

.gLFyf {
  background-color: #F1F3F4;
}

.RNNXgb:hover {
  box-shadow: none;
}

/* search bar side cogs */

.RNNXgb:before, .RNNXgb:after {
  content: "";
  position: absolute;
  background: url(https://i.imgur.com/tT2gOyK.png);
  width: 26px;
  height: 57px;
  background-size: cover;
  margin-top: -7px;
}

.RNNXgb:before {
  left: -13px;
}

.RNNXgb:after {
  right: -15px;
  transform: rotateY(180deg);
}

/* search bar top and bottom */

.SDkEP:before, .SDkEP:after {
  content: "";
  width: 100%;
  background-size: cover;
  position: absolute;
  margin-left: -16px;
}

.SDkEP:before {
  background: url(https://i.imgur.com/XsfILqf.png);
  height: 15px;
  margin-top: -15px;
}

.SDkEP:after {
  background: url(https://i.imgur.com/ZPgt6Xx.png);
  height: 10px;
  bottom: 0;
  margin-bottom: -3px;
}
}
