css:
@-moz-document url-prefix(https://www.tumblr.com/) {

body:not(#dashboard_iframe):not(#tinymce):not(#guce_index),.flag--accessibility-design-update .l-header-container--refresh, .l-container.l-container--two-column-dashboard .l-content,  .l-container.l-container--two-column .l-content,  .l-container.l-container--two-column-dashboard .right_column, .l-container.l-container--two-column .right_column,  .l-container.l-container--two-column-dashboard .left_column, .l-container.l-container--flex .l-content,  .post_avatar,  .post_avatar .post_avatar_link,  .toastr .toast, .showcase .dashboard-section,.l-header-container--refresh {background-color:#36465D;}

html.submit-form-context body {background:transparent!important;}


 .controls_section.user_list li .follow_list_item_blog .tumblelog_title {color: hsla(0,0%,100%,.6);}

.identity .controls_section.follow_list {min-height: 350px;}

 .radar .radar_tumblelog_label .name {color: rgba(255,255,255,0.5);}



 .post-forms-glass, body.flag--always-opaque-peepr.flag--accessibility-design-update .ui_peepr_glass, .blue#glass_overlay,  .blue#hello_glass, .flag--accessibility-design-update .blue.glass, .ui_dialog_lock, .blue#glass_overlay, .blue#hello_glass, .blue.glass, body.flag--always-opaque-peepr .ui_peepr_glass, body:not(#dashboard_iframe) .post-forms-glass.active {background:rgba(54,70,93,.95);}

.controls_section span.count, .identity .controls_section a .count{color:rgba(255,255,255,0.25);}

.identity .user_list li .follow_list_item_blog:before {background:transparent;border-right:none;}

 .radar .radar_footer .radar_avatar::before {background:transparent;}

 .right_column:after {display:none!important;}

 a .hide_overflow {color:rgba(255,255,255,0.6);}

body:not(#dashboard_iframe) .post-forms-glass {background:inherit;}

body#dashboard_iframe div.buttons-container .tx-icon-button::before {color:#fff;}

body#dashboard_iframe .tx-button--tertiary, body#dashboard_iframe button.follow-button, body#dashboard_iframe .tx-button.subscribe-button {background:#fff;color:#000;}

.identity .controls_section.user_list li .follow_list_item_blog:before {background:transparent;border-right:none;}

 .new_post_label i.icon_post_video {color:#748089;}

 .new_post_label i.icon_post_audio {color:#a77dc2;}

 .new_post_label i.icon_post_photo {color: #d95e40;}

 .new_post_label i.icon_post_quote {color: #f2992e;}

 .new_post_label i.icon_post_text {color:#5f5f5f;}
 .new_post_label i.icon_post_link,  .post_controls .post_control.reblog.reblogged:before {color: #56bc8a;}
 .new_post_label i.icon_post_chat {color: #529ecc;}

.ace-tumblr .ace_variable, .ace-tumblr .ace_support {color:#f2992e!important;}
.ace_tumblr.ace_tag  {color:#529ecc!important;}

.chrome.blue {border-color:#529ecc;}

.ace_tumblr.ace_prefix {color:#a77dc2!important;}

.ace_constant.ace_language, .ace-tumblr .ace_string {color: #56bc8a!important;}

.ace-tumblr .ace_storage.ace_type, ace-tumblr .ace_support.ace_type  {color:#fff!important;}

.ace_constant.ace_numeric, .ace_constant.ace_boolean {color:#dcff2c!important;}

.ace-tumblr .ace_css_constant, .ace-tumblr .ace_css_variable {color:#cdcdcd!important;}

.ace-tumblr .ace_keyword {color:#9cd2ff!important;}

 .audio-player {background: #a77dc2;}

.ui_notes .activity-notification .activity-notification__icon .ui_post_badge.audio {background-color:#a77dc2;}


@media only screen and (-webkit-min-device-pixel-ratio: 1.5), not all, only screen and (min-resolution: 1.5dppx), only screen and (min-resolution: 144dpi) {
     .tab-post-type .post-type-icon {background-image: url("https://assets.tumblr.com/images/quick_compose_sprite_2x.png?v=f40a6943f872188fc06eb64562dff406");
    background-size: 200px 700px;}
}

 .tab-post-type .post-type-icon {
    background-image:url("https://assets.tumblr.com/images/quick_compose_sprite.png?v=a9092a0967f882ab67dd7e721f555044");
}

 .post_full .post_info .post_info_fence .reblog_source .post_info_link,  .post .post_info .reblog_icon,  .post_controls .post_control:before,  .post-form--header .tumblelog-select .reblog_name,  .post-form--header .tumblelog-select .icon_arrow_carrot_down {color:#8f8f8f;}

 .post-form--header .tumblelog-select .reblog-icon {opacity:0.5;}

 .compose-button {fill: #529ecc;}

 .ui_search .search_query {color: hsla(0,0%,100%,.6);background: rgba(0,0,0,.13);}

 .reblog .post-activity-note-avatar .post-activity-avatar-link:after {background-color: #56bc8a;}
 .like .post-activity-note-avatar .post-activity-avatar-link:after,  .notification_like .avatar_frame::before, .notes .note.like .avatar_frame::before,  .ui_avatar .frame.like::before {background-color: #d95e40;}

 .answer .post-activity-note-avatar .post-activity-avatar-link:after,  .reply .post-activity-note-avatar .post-activity-avatar-link:after,  .chrome.blue, .blog-selector-container,  .selected-blog .caret.has-badge,  .blog-selector-container .selected-blog .caret.has-badge,  .ui_dialog .chrome.blue,  .notification_follower .avatar_frame::before,  .notification_group_member .avatar_frame::before,  .notes .note.follower .avatar_frame::before,  .ui_avatar .frame.follower::before,  .notification_follower .avatar_frame::before,  .notification_group_member .avatar_frame::before,  .notes .note.follower .avatar_frame::before,  .ui_avatar .frame.follower::before, .tab_notice {background-color: #529ecc;}

 .messaging-inbox .inbox-compose-toggle .compose-start, .messaging-inbox , .inbox-compose-toggle .compose-start,  .ui_notes .activity-notification .activity-notification__icon .note_follow,  .reblog_follow_button .follow-text,  .worded-follow-button .follow-text {color: #529ecc;}

 .post_controls .post_control.like.liked:before {color: #d95e40;}

 .post-form--controls .controls-container .post-form--save-button .split-button .button-area.disabled,  .post-form--controls .controls-container .post-form--save-button .split-button .button-area.disabled.active,.post-form--controls .controls-container .post-form--save-button .split-button .button-area[disabled],  .post-form--controls .controls-container .post-form--save-button .split-button .button-area[disabled]:active,  .post-form--controls .controls-container .post-form--save-button .split-button .dropdown-area.disabled,  .post-form--controls .controls-container .post-form--save-button .split-button .dropdown-area.disabled.active,  .post-form--controls .controls-container .post-form--save-button .split-button .dropdown-area[disabled],  .post-form--controls .controls-container .post-form--save-button .split-button .dropdown-area[disabled]:active,  .post-form--controls .controls-container .post-form--save-button .split-button .button-area,  .post-form--controls .controls-container .post-form--save-button .split-button .dropdown-area {    border-color: #529ecc;background-color: #529ecc;}

.inbox-sender-blog-selector .selected-blog, .activity-popover-tumblelog-select-container .selected-blog {background:#fff;}

 .showcase .login-section .about-tumblr-btn,  .showcase .about-section {background: #56bc8a;}

 .showcase .blogs-section {background: #a77dc2;}

 .showcase .create-section {background: #f2992e;}


}
