css:
@-moz-document url-prefix('https://www.tumblr.com/'), url-prefix('http://www.tumblr.com/') {
body {
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
    font-smoothing: antialiased;
}

.post_body,
.post_title {
    color: #2b2b2b!important;
}

#left_column.left_column.white_pane,
#settings_form,
#tinymce {
    background: #fff!important;
    background-color: #fff!important;
}

body,
#dashboard_index,
div.l-header-container.l-header-container--refresh,
iv.l-container.l-container--two-column-dashboard,
div.l-content.clearfix,
#permalink::after {
    background: #36465D!important;
}

div.post-forms-glass.post-forms-glass_active.active, .ui_peepr_glass {
    background: #36465de6!important;
}

#toaster .toast .toast_border,
.discover .blog_row:hover .info:before,
#left_column,
#right_colmn,
div.post_avatar.show_user_menu.post-avatar--sticky,
div.post_avatar.show_user_menu.post-avatar--sticky,
#post_avatar,
#right_column,
#dashboard_iframe,
.discover .blog_row:hover .info:before,
a.follow_list_item_blog.blog.has_follow_button::before,
.post_avatar,
.rapid-recs,
.toast-bread{
    background-color: transparent !important;
    border-color: transparent!important;
    background-image: none!important;
}

div.toastr, #body > div.l-content, #body > div.toastr > .multi-toasts, #body {
    background: #fff!important;
}

.identity-refresh .post_avatar .post_avatar_link,
#new_post > div,
div.post_avatar.post-avatar--sticky {
    background-color: transparent !important;
    border-color: transparent!important;
}

.section_header,
.section_header selected {
    text-transform: capitalize!important;
}

li.section_header,
.tumblelog_title {
    color: #b7b7b7!important;
}

div.info-container > div > h3 {
    color: #646464!important;
}

a.follow.plus-follow-button {
    color: #36465D!important;
}

div.name-container::after,
a.compose-toggle.compose-start,
a.reblog_follow_button,
a#reblog_follow_button {
    color: #477ac4!important;
}

#new_post_notice_container,
#messaging_button > div,
#inbox_button > div,
#dashboard_index > div.messaging-conversation.messaging-conversation--minimized.avatar-shape--circle,
div.conversation-minimized-badge.tab_notice.tab-notice--outlined.status-indicator-active.tab-notice--active {
    border: rgba(0, 0, 0, 0)!important;
    padding-top: 2px!important;
}
}
