css:
@-moz-document domain("taobao.com") {
/* 隐藏不需要的 */
.tbh-nav,
.layer,
.screen-outer,
.tb-footer,
.fixedtool,
.search-hots-sline,
.tbh-qr-wrapper,
.search-ft,
.tbh-corner,
.corner-inner,
.site-nav-mobile,
.site-nav-sitemap,
.site-nav-service,
.site-nav-pipe,
.site-nav-seller,
.site-nav-catalog,
#J_SiteFooter{
    display: none !important;
}

body{
    height: 100vh;
    background: white;
}

/* 搜索框 */
.J_Top{
    height: 62px;
}

.J_Cup{
    position: fixed;
    top: 50%;
    left: 50%;
    transform: translate3d(-50%, -50%, 0);
}

.tbh-search{
    width: 630px !important;
    margin: 0px !important;
    position: absolute;
    left: 50%;
    transform: translateX(-50%);
    
}

/* LOGO */
.tbh-logo{
    position: absolute;
    top: -160px;
    left: 50%;
    transform: translateX(-50%);
}

/* 顶部nav */
.J_Cover{
    position: fixed;
    top: 55%;
    left: 50%;
    transform: translateX(-50%);
}

.site-nav{
    background-color: white;
    border: none;
}

.site-nav-bd{
    width: 630px !important;
    background-color: white !important;
}
}
