css:
@-moz-document url-prefix("https://vanillo") {
.bg-color {
    background : #e866a0 ! important;
}
.search-btn {
    color: #e866a0 !important;
}
.list-group-item.active {
    background-color: #e866a0 !important;
    border-color: #e866a0 !important;
}
*.btn-sm {
    color: white;
    background: #e866a0 !important;
    background-color: #e866a0 !important;
    border-color: #e866a0 !important;
}
div > .btn {
    color: white;
    background: #e866a0 !important;
    background-color: #e866a0 !important;
    border-color: #e866a0 !important;
}
.btn-primary:hover {
    color: white;
    background: #ff7db7 !important;
    background-color: #ff7db7 !important;
    border-color: #ff7db7 !important;
}
*.btn-warning {
    color: white;
    background: #e866a0 !important;
    background-color: #e866a0 !important;
    border-color: #e866a0 !important;
}
.bg-primary > *.btn-secondary {
    color: white;
    background: #e866a0 !important;
    background-color: #e866a0 !important;
    border-color: #e866a0 !important;
}
*.bg-vanillo {
    color: white;
    background: #e866a0 !important;
    background-color: #e866a0 !important;
    border-color: #e866a0 !important;
}
*.vjs-play-progress {
    color: white;
    background: #e866a0 !important;
    background-color: #e866a0 !important;
    border-color: #e866a0 !important;
}
*.vjs-volume-level {
    color: white;
    background: #e866a0 !important;
    background-color: #e866a0 !important;
    border-color: #e866a0 !important;
}
li.vjs-selected {
    color: white;
    background: #e866a0 !important;
    background-color: #e866a0 !important;
    border-color: #e866a0 !important;
}
li.vjs-selected > span {
    color: white;
}
*.bg-vanillo > footer > div > div > a {
    color: white;
}
.fa-check-circle {
    color : #e866a0 !important;
}
}
