css:
@-moz-document domain("phys.org") {
.notice,
.google-plus,
.facebook-like,
.container-ads {
    display: none !important;
}

body {
	background-color: #222;
    color: #999;
}

#wrapper {
    background: none repeat scroll 0 0 #000;
}

.header-bottom {
    background-color: #262626;
    border-bottom: 1px solid #4b4b4b;
}

.btn-info {
	color: #fff;
	background-color: #3e3e3e;
	border-color: #4680ee;
}

textarea,
input {
    background-color: #333;
}

#sort-nav > li a {
    background-color: #222 !important;
}

.panes .button,
.post-rating .post-rate-link,
.inl-button {
    background-color: #1e1e1e;
    border: 1px solid #636363;
    color: #717171;
}

.tabs li a {
    background-color: #000;
    border-bottom: 1px solid #363636;
    border-right: 1px solid #5d5d5d;
    box-shadow: 0 -1px 0 0 #4d4d4d inset;
    color: #5a5a5a;
}

.tabs li a.current {
    background-color: #000;
    border-bottom: 1px solid #4e4e4e;
}

.tabs li a.current {
    border-left: 1px solid #535353;
}

.sorted-news-list {
	background: #111;
	border: 1px solid #444;
	-webkit-box-shadow: 0 0 2px 0 rgba(4,4,4,.08);
	box-shadow: 0 0 2px 0 rgba(4,4,4,.08);
}

.sorted-article:not(:last-child) {
	border-bottom: 1px solid #444;
}

.accent, .news-block, .page-block, .partners, .selection {
	border: 1px solid #444;
	background-color: #111;
}

.categories-content__pane::after {
	background: unset;
}

#main-tabs {
    border: 1px solid #626262;
}

.head {
    background: none repeat scroll 0 0 #000;
    border-top: 1px solid #575757;
}


.header-other {
    background-color: #151515;
    border-top: 1px solid #515151;
    color: #5a5a5a;
}

.header-other {
    background-color: #151515;
    border-top: 1px solid #515151;
    color: #5a5a5a;
}


.news-detail-box > .news-box-text::after {
    border-bottom: 1px dotted #575757;
}



p {
    color: #a3a3a3 !important;
}

p {
    line-height: 1.4;
    padding: 0 0 17px;
}

.a_editorial {
    background-color: #000;
}

.a_report {
    background-color: #000;
}

.a_weblog {
    background-color: #000;
}
#container,
.text_container_r {
    background: #2C2C2C !important;
}
.article-block {
    background: #333 !important;
}
p {
    color: #eee !important;
}
a {
    color: steelblue !important;
}
h1,
h2,
h3,
h4,
h5,
.info,
.details {
    color: #ccc !important;
}
.tabs *,
.header_l,
.header_r {
    background: #1e1e1e !important;
}
.tabs .current,
.head {
    background: #111 !important;
}

.footer-main {
    background-color: #000;
    border-top: 1px solid #505050;
}

.copy-box {
    background-color: #1b1b1b;
}
.list-category li:hover > a {
	background-color: #009df2;
	text-decoration: none;
    color: #fff!important;
}

.footer-partners {
	border-top: 1px solid #444;
}

.bg-info {
	background-color: #000 !important;
}

.bg-white {
	background-color: #111 !important;
}

.article-main__more {
	background-color: #000;
}

.bg-light {
	background-color: #222 !important;
}

.sorted-row:not(:last-child) > [class^="col-"]::after {
	border-bottom: 1px solid #444;
}

.form-control {
	color: #999;
	background-color: #333;
	border: 1px solid #444;
}
}
