css:
@-moz-document url-prefix("https://jira.upcnet.es") {

/* Fer més gran el Create Plan del timeline */
#tp-timeline-sidebar {
    transition: width 1s ease-in .4s;
}
#tp-timeline-sidebar:hover {
    width:60%;
    transition: width 1s ease 0s;
}

/* fer més gran el panell lateral del kanban */
#ghx-detail-view {
    transition: width 1s ease-in .4s;
    z-index: 3;
    position:relative;
}
#ghx-detail-view:hover {
    width:800px!important;
    transition: width 1s ease 0s;
}

/* Fer més grans els selectors de projecte i issue del boto "create" */
#issuetype-single-select, #project-single-select,
form.aui #issuetype-single-select  .text,
form.aui #project-single-select .text{
    max-width: 500px;
}

/* marcar més visiblement el current day del planner */
.tl-current-day {
    background-color: #c3ccce;
}

.tl-day[type=NON_WORKING_DAY] {
    background-color: #e8d2e4;
}

}
